import Pagination from '@/components/Pagination/index';
export default {
  name: 'ReviewArrest',
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      form: {
        leibie: '',
        state: '',
        name: '',
        start: '',
        end: ''
      },
      // tableData: [],
      listQuery: {
        pageNum: 1,
        pageSize: 10,
        keyWord: ''
      },
      tableHeader: [{
        filed: 'visitorName',
        title: '咨询人姓名',
        colorB: 'colorB',
        width: '200'
      }, {
        filed: 'mobile',
        title: '手机号码'
      }, {
        filed: 'createTime',
        title: '咨询时间'
      }, {
        filed: 'optionValueName',
        title: '咨询类型'
      }, {
        filed: 'isReply',
        title: '状态'
      }],
      tableData: [],
      listLoading: true,
      total: 100
    };
  },
  created: function created() {
    this.getTime();
  },
  mounted: function mounted() {
    this.getList();
  },
  methods: {
    handleFilter: function handleFilter() {
      var _this = this;

      for (var k in this.form) {
        if (this.form[k] == '') {
          this.$message.error('请输入完整信息后在查询');
          return false;
        }
      }

      this.$api.getWlzixunList({
        visitorName: this.form.name,
        startTime: this.form.start,
        endTime: this.form.end,
        optionValue: this.form.leibie,
        state: this.form.state,
        pageNum: 1,
        pageSize: 10
      }).then(function (res) {
        _this.tableData = res.data;
      }).catch(function (err) {
        console.log(err);
      });
    },
    getList: function getList() {
      this.listLoading = false;
    },
    handlewatch: function handlewatch(row) {
      this.$router.push({
        name: 'wlzxxq',
        params: {
          id: row.id,
          length: row.isReply.length
        }
      });
    },
    refresh: function refresh(val) {},
    getTime: function getTime() {
      var nowDate = new Date();
      var date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      };
      var mouth = ''; // 月

      if (date.month < 10) {
        mouth = '0' + date.month;
      } else {
        mouth = date.month;
      } // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }


      this.form.start = date.year - 1 + '-' + mouth;
      this.form.end = date.year + '-' + mouth;
    }
  }
};
