import Pagination from '@/components/Pagination/index';
export default {
  name: 'ReviewArrest',
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      anjianinfo: [],
      caseVisible: false,
      isShowClose: false,
      caseShow: false,
      infoPushVisible: false,
      xingfangId: '',
      caseTitle: '共享',
      checdList: [],
      gongXiangListName: [],
      value: [],
      options: [],
      props: {
        multiple: true,
        checkStrictly: true,
        emitPath: false,
        label: 'name',
        value: 'id',
        children: 'children'
      },
      bianGenInfo: {
        czrymc: '',
        bgrq: '',
        optContent: ''
      },
      form: {
        leixing: '',
        state: '',
        time: ['', ''],
        end: '',
        name: ''
      },
      // tableData: [],
      listQuery: {
        page: 1,
        pageSize: 10,
        keyWord: ''
      },
      tableHeader: [{
        filed: 'lawyerName',
        title: '律师姓名',
        colorB: 'colorB',
        width: '200'
      }, {
        filed: 'mobile',
        title: '手机号码'
      }, {
        filed: 'handleTime',
        title: '接访时间'
      }, {
        filed: 'visitorName',
        title: '信访人姓名'
      }, {
        filed: 'optionValueName',
        title: '信访类型'
      }, {
        filed: 'procuratorateName',
        title: '信息来源'
      }],
      tableData: [],
      listLoading: false,
      total: 0
    };
  },
  created: function created() {
    var _this = this;

    // this.getTime()
    this.$api.getjianchanyuanLIst().then(function (res) {
      if (res.code === 0) {
        _this.options = res.data;
      }
    });
  },
  mounted: function mounted() {// this.getList()
  },
  watch: {
    value: function value(newValue, oldValue) {
      console.log(this.options);
      console.log(newValue);
      this.checdList = [];
      this.nameList = [];
      var that = this;

      for (var i = 0; i < newValue.length; i++) {
        var id = newValue[i];
        this.options.map(function (item) {
          if (item.id == id) {
            that.checdList.push(item);
            that.$api.getjianchanyuanChildren(item.id).then(function (res) {
              if (res.code === 0) {
                that.$set(item, 'children', res.data);
              }
            });
          } else {
            if (item.children) {
              that.$options.methods.finditem(item.children, id, that);
            }
          }
        });
      }
    }
  },
  methods: {
    finditem: function finditem(list, id, that) {
      console.log(list);
      list.map(function (item) {
        if (item.id == id) {
          that.checdList.push(item);

          if (item.hasChildren == 1) {
            that.$api.getjianchanyuanChildren(item.id).then(function (res) {
              console.log(res);

              if (res.code === 0) {
                that.$set(item, 'children', res.data);
                console.log(that.options);
              }
            });
          }
        } else {
          if (item.children) {
            that.$options.methods.finditem(item.children, id, that);
          }
        }
      });
    },
    // 搜索网络咨询接口
    handleFilter: function handleFilter() {
      var _this2 = this;

      this.listQuery.page = 1;
      this.listQuery.pageSize = 10;
      this.listLoading = true;
      this.$api.chuangkousousuo({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leixing,
        state: this.form.state,
        pageNum: 1,
        pageSize: 10
      }).then(function (res) {
        console.log(res);

        if (res.code === 0) {
          if (res.data) {
            _this2.tableData = res.data.pageList;
            _this2.total = res.data.total;
            _this2.listLoading = false;
          } else {
            _this2.$message.warning('暂无数据哦');

            _this2.tableData = [];
            _this2.listLoading = false;
          }
        } else {
          _this2.$message.error('获取数据失败');

          _this2.listLoading = false;
        }
      });
    },
    getList: function getList() {
      this.listLoading = false;
    },
    chongzhi: function chongzhi() {
      this.form.name = '';
      this.form.state = '';
      this.form.leixing = '';
      this.form.time = ['', ''];
    },
    displayPop: function displayPop() {
      this.caseVisible = false;
    },
    gongxiang: function gongxiang() {
      var _this3 = this;

      var list = [];
      list.push(this.xingfangId);
      this.$api.gongxianghuajie({
        list: list,
        procuratorateList: this.value
      }).then(function (res) {
        if (res.code === 0) {
          _this3.$message.success('共享成功');

          _this3.caseVisible = false;
          _this3.value = [];
          _this3.checdList = [];
        } else {
          _this3.$message.error('共享失败');
        }
      });
    },
    handleClick: function handleClick(row) {
      this.$router.push({
        name: 'ajhjgzxq',
        params: {
          id: row.id
        }
      });
    },
    handleSee: function handleSee(row) {
      this.xingfangId = row.id;
      this.caseVisible = true;
      this.caseShow = true;
    },
    refresh: function refresh(val) {
      var _this4 = this;

      this.listLoading = true;
      this.$api.chuangkousousuo({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leixing,
        state: this.form.state,
        pageNum: val.page,
        pageSize: val.limit
      }).then(function (res) {
        console.log(res);

        if (res.code === 0) {
          if (res.data) {
            _this4.tableData = res.data.pageList;
            _this4.total = res.data.total;
            _this4.listLoading = false;
          } else {
            _this4.$message.warning('暂无数据哦');

            _this4.tableData = [];
            _this4.listLoading = false;
          }
        } else {
          _this4.$message.error('获取数据失败');

          _this4.listLoading = false;
        }
      });
    },
    getTime: function getTime() {
      var nowDate = new Date();
      var date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      };
      var mouth = ''; // 月

      if (date.month < 10) {
        mouth = '0' + date.month;
      } else {
        mouth = date.month;
      } // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }


      this.form.start = date.year - 1 + '-' + mouth;
      this.form.end = date.year + '-' + mouth;
    }
  }
};
