import Pagination from '@/components/Pagination/index';
export default {
  name: 'ReviewArrest',
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      form: {
        leibie: '',
        state: '',
        name: '',
        time: ['', '']
      },
      // tableData: [],
      listQuery: {
        page: 1,
        pageSize: 10,
        keyWord: ''
      },
      tableHeader: [{
        filed: 'visitorName',
        title: '咨询人姓名',
        colorB: 'colorB',
        width: '200'
      }, {
        filed: 'mobile',
        title: '手机号码'
      }, {
        filed: 'createTime',
        title: '咨询时间'
      }, {
        filed: 'optionValueName',
        title: '咨询类型'
      }, {
        filed: 'isReply',
        title: '状态'
      }],
      tableData: [],
      listLoading: false,
      total: 100
    };
  },
  created: function created() {
    this.getTime();
  },
  mounted: function mounted() {},
  methods: {
    handleFilter: function handleFilter() {
      var _this = this;

      this.listLoading = true;
      this.listQuery.page = 1;
      this.listQuery.pageSize = 10;
      this.$api.getWlzixunList({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leibie,
        state: this.form.state,
        pageNum: 1,
        pageSize: 10
      }).then(function (res) {
        console.log(res);

        if (res.code === 0) {
          if (res.data) {
            _this.tableData = res.data.pageList;
            _this.total = res.data.total;
            _this.listLoading = false;
          } else {
            _this.$message.warning('暂无数据哦');

            _this.listLoading = false;
          }
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    chongzhi: function chongzhi() {
      this.form.leibie = '';
      this.form.state = '';
      this.form.time = ['', ''];
      this.form.name = '';
    },
    handlewatch: function handlewatch(row) {
      this.$router.push({
        name: 'wlzxxq',
        params: {
          id: row.id,
          length: row.isReply
        }
      });
    },
    refresh: function refresh(val) {
      var _this2 = this;

      this.$api.getWlzixunList({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leibie,
        state: this.form.state,
        pageNum: val.page,
        pageSize: val.limit
      }).then(function (res) {
        console.log(res);

        if (res.code === 0) {
          if (res.data) {
            _this2.tableData = res.data.pageList;
            _this2.total = res.data.total;
            _this2.listLoading = false;
          } else {
            _this2.$message.warning('暂无数据哦');

            _this2.listLoading = false;
          }
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    getTime: function getTime() {
      var nowDate = new Date();
      var date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1,
        date: nowDate.getDate()
      };
      var mouth = '';
      var day = ''; // 月

      if (date.month < 10) {
        mouth = '0' + date.month;
      } else {
        mouth = date.month;
      } // 日


      if (date.date < 10) {
        day = '0' + date.date;
      } else {
        day = date.date;
      }

      this.form.time[0] = date.year - 1 + '-' + mouth + '-' + day;
      this.form.time[1] = date.year + '-' + mouth + '-' + day;
    }
  }
};
