export default {
  data: function data() {
    return {
      form: {
        name: '',
        jianchayuan: '',
        number: '',
        leixing: '1',
        sfz: '',
        card: '',
        conten: ''
      },
      fileList: [],
      file1: {},
      file2: {},
      file3: {},
      file4: {},
      rules: {
        name: [{
          required: true,
          message: '请输入姓名',
          trigger: ['blur', 'change']
        }],
        jianchayuan: [{
          required: true,
          message: '请选择检察院',
          trigger: ['blur', 'change']
        }],
        number: [{
          required: true,
          message: '请输入手机号码',
          trigger: ['blur', 'change']
        }],
        leixing: [{
          required: true,
          message: '请选择证件类型',
          trigger: ['blur', 'change']
        }],
        sfz: [{
          required: true,
          message: '请输入身份证',
          trigger: ['blur', 'change']
        }],
        card: [{
          required: true,
          message: '请输入执证号码',
          trigger: ['blur', 'change']
        }],
        tupian: [{
          required: true,
          message: '请选择活',
          trigger: ['blur', 'change']
        }],
        conten: [{
          required: true,
          message: '请输入律师简介',
          trigger: ['blur', 'change']
        }, {
          min: 10,
          max: 50,
          message: '长度在 10 到 50 个字符',
          trigger: ['blur', 'change']
        }]
      },
      labelPosition: 'right',
      dialogImageUrl: '',
      dialogVisible: false,
      disabled: false,
      tongXingZheng: false
    };
  },
  methods: {
    onblurnum: function onblurnum() {
      var _this = this;

      this.$api.getjianchanlvshnum(this.form.number).then(function (res) {
        if (res.code === 0) {
          _this.$message.success('手机号码校验通过');

          _this.tongXingZheng = true;
        } else {
          _this.$message.error('手机号码校验不通过, 请重新输入');

          _this.tongXingZheng = false;
        }
      });
    },
    onblursfz: function onblursfz() {
      var _this2 = this;

      this.$api.getjianchanlvshsfz(this.form.sfz).then(function (res) {
        if (res.code === 0) {
          _this2.$message.success('身份证号码校验通过');

          _this2.tongXingZheng = true;
        } else {
          _this2.$message.error('身份证校验不通过, 请重新输入');

          _this2.tongXingZheng = false;
        }
      });
    },
    onblurcard: function onblurcard() {
      var _this3 = this;

      this.$api.getjianchanlvshcard(this.form.card).then(function (res) {
        if (res.code === 0) {
          _this3.$message.success('执业证号码校验通过');

          _this3.tongXingZheng = true;
        } else {
          _this3.$message.error('执业证号码校验不通过, 请重新输入');

          _this3.tongXingZheng = false;
        }
      });
    },
    handleRemove: function handleRemove(file) {
      console.log(file);
    },
    handlePictureCardPreview: function handlePictureCardPreview(file) {
      this.dialogImageUrl = file.url;
      this.dialogVisible = true;
    },
    handleDownload: function handleDownload(file) {
      console.log(file);
    },
    // beforeupload (file) {
    //   this.formData.append('file', file)
    //   return false
    // },
    handleChange: function handleChange(file, fileList) {
      console.log(file);
      console.log(fileList);
      console.log(this.fileList);
    },
    beforeupload1: function beforeupload1(conten) {
      console.log(conten.file);
      this.file1 = conten.file;
    },
    beforeupload2: function beforeupload2(conten) {
      console.log(conten.file);
      this.file2 = conten.file;
    },
    beforeupload3: function beforeupload3(conten) {
      console.log(conten.file);
      this.file3 = conten.file;
    },
    beforeupload4: function beforeupload4(conten) {
      console.log(conten.file);
      this.file4 = conten.file;
    },
    // const isJPG = file.type === 'image/jpeg';
    // const isLt2M = file.size / 1024 / 1024 < 2;
    // if (!isJPG) {
    //   this.$message.error('上传头像图片只能是 JPG 格式!');
    // }
    // if (!isLt2M) {
    //   this.$message.error('上传头像图片大小不能超过 2MB!');
    // }
    // return isJPG && isLt2M;
    goback: function goback() {
      this.$router.go(-1);
    },
    submitForm: function submitForm(form) {
      var _this4 = this;

      if (!this.tongXingZheng) {
        this.$message.error('手机、身份证或执业证校验未通过,请从重新输入');
        return false;
      }

      if (!this.file1.name) {
        this.$message.error('请上传身份证正面');
        return false;
      }

      if (!this.file2.name) {
        this.$message.error('请上传身份证背面');
        return false;
      }

      if (!this.file3.name) {
        this.$message.error('请上传律师资格证');
        return false;
      }

      if (!this.file4.name) {
        this.$message.error('请上传律师形象照');
        return false;
      }

      this.$refs[form].validate(function (valid) {
        if (valid) {
          var formData = new FormData();
          formData.append("lawyerName", _this4.form.name);
          formData.append("procuratorateId", localStorage.getItem('danweiID'));
          formData.append("mobile", _this4.form.number);
          formData.append("certificateOption", _this4.form.leixing);
          formData.append("idcardNum", _this4.form.sfz);
          formData.append("professionNum", _this4.form.card);
          formData.append("simpleMemo", _this4.form.conten);
          formData.append("idcardImgFront", _this4.file1);
          formData.append("idcardImgBack", _this4.file2);
          formData.append("certificateImg", _this4.file3);
          formData.append("photoImg", _this4.file4);

          _this4.$api.uploadlvhsi(formData).then(function (res) {
            _this4.$message.success('添加成功');

            _this4.$router.push({
              name: 'lszyk'
            });
          });
        } else {
          _this4.$message.error('请输入完整信息');

          return false;
        }
      });
    }
  }
};
