import Pagination from '@/components/Pagination/index';
export default {
  name: 'ReviewArrest',
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      infoPushVisible: false,
      form: {
        state: '',
        time: [],
        name: ''
      },
      // tableData: [],
      listQuery: {
        page: 1,
        pageSize: 10,
        keyWord: ''
      },
      tableHeader: [{
        filed: 'title',
        title: '标题',
        colorB: 'colorB',
        width: '200'
      }, {
        filed: 'updateTime',
        title: '咨询时间'
      }, {
        filed: 'typeName',
        title: '栏目'
      }, {
        filed: 'state',
        title: '状态'
      }],
      tableData: [],
      listLoading: false,
      total: 0
    };
  },
  created: function created() {},
  mounted: function mounted() {},
  methods: {
    chongzhi: function chongzhi() {
      this.form.name = '';
      this.form.state = '';
      this.form.time = ['', ''];
    },
    handleFilter: function handleFilter() {
      var _this = this;

      this.listQuery.pageSize = 10;
      this.listQuery.page = 1;
      this.listLoading = true;
      this.$api.getgonggao({
        title: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        state: this.form.state,
        pageNum: 1,
        pageSize: 10
      }).then(function (res) {
        console.log(res);

        if (res.code === 0) {
          if (res.data) {
            _this.tableData = res.data.pageList;

            _this.tableData.map(function (item) {
              if (item.state == 1) {
                item.state = '已发布';
              }

              if (item.state == 0) {
                item.state = '草稿';
              }
            });

            _this.total = res.data.total;
            _this.listLoading = false;
          } else {
            _this.$message.warning('暂无数据哦');

            _this.listLoading = false;
            _this.tableData = [];
          }
        } else {
          _this.$message.error('获取数据失败');
        }
      });
    },
    handleAdd: function handleAdd() {
      this.$router.push({
        name: 'xxtj'
      });
    },
    getList: function getList() {
      this.listLoading = false;
    },
    handleSee: function handleSee(row) {
      this.$router.push({
        name: 'chakan',
        params: {
          id: row.id
        }
      });
    },
    handleChange: function handleChange(row) {
      this.$router.push({
        name: 'xiugai',
        params: {
          id: row.id
        }
      });
    },
    handleDel: function handleDel(row) {
      var _this2 = this;

      this.$confirm('你是否确认删除选中项?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        customClass: 'message-manage',
        type: 'warning'
      }).then(function () {
        _this2.$api.chanchu(row.id).then(function (res) {
          if (res.code === 0) {
            var index = _this2.tableData.findIndex(function (item) {
              return item.id === row.id;
            });

            _this2.tableData.splice(index, 1);

            _this2.$message.success('删除成功');
          } else {
            _this2.$message.error('删除失败');
          }
        });
      });
    },
    handleSend: function handleSend(row) {
      var _this3 = this;

      this.$confirm('你是否确认发布选中项?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        customClass: 'message-manage',
        type: 'warning'
      }).then(function () {
        _this3.$api.xiugai(row.id, {
          state: '1'
        }).then(function (res) {
          if (res.code === 0) {
            _this3.$message.success('发布成功');

            row.state = '已发布';
          } else {
            _this3.$message.error('发布失败');
          }
        });
      });
    },
    refresh: function refresh(val) {
      var _this4 = this;

      this.listLoading = true;
      this.$api.getgonggao({
        title: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        state: this.form.state,
        pageNum: val.page,
        pageSize: val.limit
      }).then(function (res) {
        if (res.code === 0) {
          if (res.data) {
            _this4.tableData = res.data.pageList;
            _this4.listLoading = false;
          } else {
            _this4.$message.warning('暂无数据哦');

            _this4.listLoading = false;
            _this4.tableData = [];
          }
        } else {
          _this4.$message.error('获取数据失败');
        }
      });
    },
    getTime: function getTime() {
      var nowDate = new Date();
      var date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      };
      var mouth = ''; // 月

      if (date.month < 10) {
        mouth = '0' + date.month;
      } else {
        mouth = date.month;
      } // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }


      this.form.start = date.year - 1 + '-' + mouth;
      this.form.end = date.year + '-' + mouth;
    }
  }
};
