export default {
  data: function data() {
    return {
      show: false,
      show1: false,
      show2: false,
      show3: false,
      show4: false,
      str: '',
      riqi: '',
      xingqi: '',
      username: '',
      time: '',
      biaogeshuju: {},
      data: [// {name:'',time:'',title:''},
      ],
      hours: '',
      chatData: ''
    };
  },
  methods: {
    sendinfo: function sendinfo() {
      var _this = this;

      var nowDate = new Date();
      var date = {
        hours: nowDate.getHours(),
        minu: nowDate.getMinutes(),
        second: nowDate.getSeconds()
      };
      var hours = '';
      var minu = '';
      var second = ''; //时

      if (date.hours < 10) {
        hours = '0' + date.hours;
      } else {
        hours = date.hours;
      } //分


      if (date.minu < 10) {
        minu = '0' + date.minu;
      } else {
        minu = date.minu;
      } //秒


      if (date.second < 10) {
        second = '0' + date.second;
      } else {
        second = date.second;
      }

      var alltime = hours + ':' + minu + ':' + second;
      var message = {};
      message.lawyerName = '小沫沫';
      message.createTime = alltime;
      message.content = this.chatData;
      this.data.push(message);
      this.$api.getWljiefangsendinfo({
        id: this.$route.params.id,
        content: this.chatData
      }).then(function (res) {
        _this.$message.success('回复成功');

        _this.chatData = '';
      });
    },
    beginJf: function beginJf() {
      var _this2 = this;

      this.$api.getWljiefangchange({
        id: this.$route.params.id
      }).then(function (res) {
        _this2.show1 = true;
        _this2.show4 = true;
        _this2.show = false;
      });
    },
    endJf: function endJf() {
      var _this3 = this;

      this.$api.getWljiefangchange({
        id: this.$route.params.id
      }).then(function (res) {
        _this3.show1 = false;
        _this3.show4 = false;
        _this3.show = false;
        _this3.show2 = true;
      });
    }
  },
  created: function created() {
    var _this4 = this;

    //  获取月份 星期
    this.$api.getWljiefangxq({
      id: this.$route.params.id
    }).then(function (res) {
      console.log(res);
      _this4.biaogeshuju = res.data;
      _this4.str = res.data.state; // 后台返回直接4种中文字符串 直接===判断四种

      if (_this4.str === true) {
        _this4.show = true;
      }

      if (_this4.str === false) {
        _this4.show1 = true;
        _this4.show4 = true;
      } // if (this.str===true) {
      //   this.show2 = true
      // }
      // if (this.str===true) {
      //   this.show3 = true
      // }

    });
    this.$api.getWljiefangliaotian({
      id: this.$route.params.id
    }).then(function (res) {
      console.log(res);
      _this4.data = res.data;
    });
  },
  mounted: function mounted() {}
};
