import Vue from 'vue'
import Router from 'vue-router'
import Login from '@/views/login/index'
import xsjcjdRouter from './xsjcjd'
import zhscjdRouter from './zhscjd' // 智慧审查鉴定
import zhajglRouter from './zhajgl' // 智慧案件管理路由
import tybagjRouter from './tyfzbagjjzj' // 通用辅助办案工具及组件
import znksRouter from './znks'
import jcfxypRouter from './jcfxyp'

Vue.use(Router)

import Layout from '@/layout'

export const constantRoutes = [
  {
    path: '/',
    name: 'Login',
    component: Login
  },
  {
    path: '/redirect',
    component: Layout,
    hidden: true,
    children: [
      {
        path: '/redirect/:path(.*)',
        component: () => import('@/views/redirect/index')
      }
    ]
  },
  {
    path: '/login',
    component: () => import('@/views/login/index'),
    hidden: true
  },
  {
    path: '/auth-redirect',
    component: () => import('@/views/login/auth-redirect'),
    hidden: true
  },
  {
    path: '/404',
    component: () => import('@/views/error-page/404'),
    hidden: true
  },
  {
    path: '/401',
    component: () => import('@/views/error-page/401'),
    hidden: true
  },

  // xsjcjd 智慧刑事检查
  {
    path: '/',
    component: Layout,
    redirect: '/xsjcjd',
    meta: { title: '智慧刑事检查监督', icon: 'el-icon-folder-opened' },
    children: [
      ...xsjcjdRouter
    ]
  },
  // zhscjd 智慧审查鉴定
  {
    path: '/zhscjd',
    component: Layout,
    redirect: '/zhscjd',
    meta: { title: '智慧审查鉴定', icon: 'el-icon-folder-opened' },
    children: [
      ...zhscjdRouter
    ]
  },
  // zhajgl 智慧案件管理
  {
    path: '/zhajgl',
    component: Layout,
    redirect: '/zhajgl/znsa',
    meta: { title: '智慧案件管理', icon: 'el-icon-folder-opened' },
    children: [
      ...zhajglRouter
    ]
  },
  // 通用辅助办案工具及组件
  {
    path: '/tyfzbagjjzj',
    component: Layout,
    redirect: '/tyfzbagjjzj/znxwgjx',
    meta: { title: '通用辅助办案工具及组件', icon: 'el-icon-folder-opened' },
    children: [
      ...tybagjRouter
    ]
  },
  // 智能控审模块
  {
    path: '/zhks',
    component: Layout,
    redirect: '/znks/jcg',
    meta: { title: '智能控审', icon: 'el-icon-folder-opened' },
    children: [
      ...znksRouter
    ]
  },
  // 检察建议
  {
    path: 'https://www.baidu.com/#8',
    component: Layout,
    redirect: '/jcjy/index',
    //  component: () => import('@/views/zhjqr/index'),
    meta: { title: '检察建议案件化办理', icon: 'el-icon-folder-opened' },
    children: [
      {
        path: '/jcjy/index',
        name: 'jcjyIndex',
        // redirect: '/calSupervise/index',
        component: () => import('@/views/zhjqr/index'),
        meta: { title: '检察建议案件化办理', icon: 'el-icon-folder-opened' }
      }
    ]
  },
  // 司法知识数据资源库
  {
    path: 'https://www.baidu.com/#1',
    component: Layout,
    redirect: '/sfzyk/index',
    //  component: () => import('@/views/zhjqr/index'),
    meta: { title: '司法知识数据资源库', icon: 'el-icon-folder-opened' },
    children: [
      {
        path: '/sfzyk/index',
        name: 'sfzykIndex',
        // redirect: '/calSupervise/index',
        component: () => import('@/views/zhjqr/index'),
        meta: { title: '司法知识数据资源库', icon: 'el-icon-folder-opened' }
      }
    ]
  },
  // 决策智能分析
  {
    path: '/jcfxyp',
    component: Layout,
    redirect: '/jcfxyp',
    meta: { title: '决策智能分析研判系统', icon: 'el-icon-folder-opened' },
    children: [
      ...jcfxypRouter
    ]
  },
  // 数据可视化
  {
    path: 'https://www.baidu.com/#2',
    component: Layout,
    redirect: '/sjksh/index',
    //  component: () => import('@/views/zhjqr/index'),
    meta: { title: '数据可视化服务平台', icon: 'el-icon-folder-opened' },
    children: [
      {
        path: '/sjksh/index',
        name: 'sjkshIndex',
        // redirect: '/calSupervise/index',
        component: () => import('@/views/zhjqr/index'),
        meta: { title: '数据可视化服务平台', icon: 'el-icon-folder-opened' }
      }
    ]
  },
  // 智慧餐厅管理
  {
    path: 'https://www.baidu.com/#3',
    component: Layout,
    redirect: '/zhctgl/index',
    //  component: () => import('@/views/zhjqr/index'),
    meta: { title: '智慧餐厅管理', icon: 'el-icon-folder-opened' },
    children: [
      {
        path: '/zhctgl/index',
        name: 'zhctglIndex',
        // redirect: '/calSupervise/index',
        component: () => import('@/views/zhjqr/index'),
        meta: { title: '智慧餐厅管理', icon: 'el-icon-folder-opened' }
      }
    ]
  },
  // 智慧服装管理
  {
    path: 'https://www.baidu.com/#4',
    component: Layout,
    redirect: '/zhfzgl/index',
    //  component: () => import('@/views/zhjqr/index'),
    meta: { title: '智慧服装管理', icon: 'el-icon-folder-opened' },
    children: [
      {
        path: '/zhfzgl/index',
        name: 'zhfzglIndex',
        // redirect: '/calSupervise/index',
        component: () => import('@/views/zhjqr/index'),
        meta: { title: '智慧服装管理', icon: 'el-icon-folder-opened' }
      }
    ]
  },
  // 移动管理子系统
  {
    path: 'https://www.baidu.com/#5',
    component: Layout,
    redirect: '/ydglxt/index',
    //  component: () => import('@/views/zhjqr/index'),
    meta: { title: '移动管理子系统', icon: 'el-icon-folder-opened' },
    children: [
      {
        path: '/ydglxt/index',
        name: 'ydglxtIndex',
        // redirect: '/calSupervise/index',
        component: () => import('@/views/zhjqr/index'),
        meta: { title: '移动管理子系统', icon: 'el-icon-folder-opened' }
      }
    ]
  },

]

export const asyncRoutes = [
]
// export const asyncRoutes = [
//   {
//     path: '/calSupervise',
//     name: 'calSupervise',
//     component: Layout,
//     redirect: '/calSupervise/create',
//     meta: { title: '图书管理', icon: 'el-icon-folder-opened', roles: ['admin', 'editor'] },
//     children: [
//       {
//         name: 'bookCreate',
//         path: '/book/create',
//         component: () => import('@/views/book/create'),
//         meta: { title: '上传图书', icon: 'icon-dian', roles: ['admin'] }
//       },
//       {
//         name: 'bookEdit',
//         path: '/book/edit/:fileName',
//         component: () => import('@/views/book/edit'),
//         hidden: true,
//         meta: { title: '编辑图书', icon: 'edit', roles: ['admin', 'editor'], activeMenu: '/book/list' }
//       },
//       {
//         name: 'bookList',
//         path: '/book/list',
//         component: () => import('@/views/book/list'),
//         meta: { title: '图书列表', icon: 'list', roles: ['admin', 'editor'] }
//       }
//     ]
//   },
//   { path: '*', redirect: '/404', hidden: true }
// ]

const createRouter = () => new Router({
  scrollBehavior: () => ({ y: 0 }),
  routes: constantRoutes
})

const router = createRouter()

// Detail see: https://github.com/vuejs/vue-router/issues/1234#issuecomment-357941465
export function resetRouter() {
  const newRouter = createRouter()
  router.matcher = newRouter.matcher // reset router
}

export default router
