import Pagination from '@/components/Pagination/index';
export default {
  name: 'ReviewArrest',
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      infoPushVisible: false,
      bianGenInfo: {
        czrymc: '',
        bgrq: '',
        optContent: ''
      },
      jianchayuanList: [],
      options: [],
      value: '',
      props: {
        multiple: false,
        checkStrictly: true,
        emitPath: false,
        label: 'name',
        value: 'id',
        children: 'children'
      },
      form: {
        name: '',
        ajmc: ''
      },
      // tableData: [],
      listQuery: {
        page: 1,
        pageSize: 10,
        keyWord: ''
      },
      tableHeader: [{
        filed: 'realName',
        title: '律师姓名',
        colorB: 'colorB',
        width: '200'
      }, {
        filed: 'procuratorateName',
        title: '签约检察院'
      }, {
        filed: 'idcardNum',
        title: '身份证号码'
      }, {
        filed: 'professionNum',
        title: '执业证号'
      }],
      tableData: [],
      listLoading: false,
      total: 100
    };
  },
  watch: {
    value: function value(newValue, oldValue) {
      var that = this;
      this.options.find(function (item) {
        if (item.id == newValue) {
          that.$api.getjianchanyuanChildren(item.id).then(function (res) {
            if (res.code === 0) {
              that.$set(item, 'children', res.data);
            }
          });
        } else {
          if (item.children) {
            that.$options.methods.finditem(item.children, newValue, that);
          }
        }
      });
    }
  },
  created: function created() {
    var _this = this;

    this.$api.getjianchanyuanLIst().then(function (res) {
      if (res.code === 0) {
        _this.options = res.data;
      }
    });
    this.listLoading = true;
    this.$api.getjianchanlvshi({
      name: this.form.name,
      procuratorateId: this.form.ajmc,
      pageNum: 1,
      pageSize: 10
    }).then(function (res) {
      console.log(res);

      if (res.code === 0) {
        if (res.data) {
          _this.tableData = res.data.pageList;
          _this.total = res.data.total;
          _this.listLoading = false;
        } else {
          _this.tableData = [];

          _this.$message.warning('暂无数据哦');

          _this.listLoading = false;
        }
      } else {
        _this.$message.error('获取列表失败');

        _this.listLoading = false;
      }
    });
  },
  mounted: function mounted() {// this.getList()
  },
  methods: {
    // 搜索律师资源接口
    handleFilter: function handleFilter() {
      var _this2 = this;

      this.listLoading = true;
      this.listQuery.page = 1;
      this.listQuery.pageSize = 10;
      this.$api.getjianchanlvshi({
        name: this.form.name,
        procuratorateId: this.form.ajmc,
        pageNum: 1,
        pageSize: 10
      }).then(function (res) {
        console.log(res);

        if (res.code === 0) {
          if (res.data) {
            _this2.tableData = res.data.pageList;
            _this2.total = res.data.total;
            _this2.listLoading = false;
          } else {
            _this2.tableData = [];

            _this2.$message.warning('暂无数据哦');

            _this2.listLoading = false;
          }
        } else {
          _this2.$message.error('获取列表失败');

          _this2.listLoading = false;
        }
      });
    },
    getList: function getList() {
      this.listLoading = false;
    },
    handleClick: function handleClick(row) {
      this.$router.push({
        name: 'watch',
        params: {
          id: row.id
        }
      });
    },
    handleDle: function handleDle(row) {
      this.$confirm('你是否确认删除选中项?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        customClass: 'message-manage',
        type: 'warning'
      }).then(function () {});
    },
    finditem: function finditem(list, id, that) {
      list.find(function (item) {
        if (item.id == id) {
          if (item.hasChildren == 1) {
            that.$api.getjianchanyuanChildren(item.id).then(function (res) {
              if (res.code === 0) {
                that.$set(item, 'children', res.data);
              }
            });
          }
        } else {
          if (item.children) {
            that.$options.methods.finditem(item.children, id, that);
          }
        }
      });
    },
    handleClickLook: function handleClickLook(row) {
      this.$router.push({
        name: 'change',
        params: {
          id: row.id
        }
      });
    },
    handleAdd: function handleAdd() {
      this.$router.push({
        name: 'add'
      });
    },
    refresh: function refresh(val) {
      var _this3 = this;

      this.listLoading = true;
      this.$api.getjianchanlvshi({
        name: this.form.name,
        procuratorateId: this.form.ajmc,
        pageNum: val.page,
        pageSize: val.limit
      }).then(function (res) {
        console.log(res);

        if (res.code === 0) {
          if (res.data) {
            _this3.tableData = res.data.pageList;
            _this3.total = res.data.total;
            _this3.listLoading = false;
          } else {
            _this3.tableData = [];

            _this3.$message.warning('暂无数据哦');

            _this3.listLoading = false;
          }
        } else {
          _this3.$message.error('获取列表失败');

          _this3.listLoading = false;
        }
      });
    },
    getTime: function getTime() {
      var nowDate = new Date();
      var date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      };
      var mouth = ''; // 月

      if (date.month < 10) {
        mouth = '0' + date.month;
      } else {
        mouth = date.month;
      } // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }


      this.form.start = date.year - 1 + '-' + mouth;
      this.form.end = date.year + '-' + mouth;
    }
  }
};
