import PieCharts from '@/components/Charts/PieEcharts'
import GradientBar from '@/components/Charts/barEcharts'
import LinMarker from '@/components/Charts/LineMarker'
import mapChart from '@/components/Charts/mapEcharts'
import Pagination from '@/components/Pagination/index'
// import scqk from './components/scqk'
export default {
  name: 'Dashboard',
  components: { PieCharts, mapChart, GradientBar, LinMarker, Pagination },
  data() {
    return {
      listQuery: {},
      pieData: [],
      progressData0: {},
      progressData1: {},
      custom: '#5ce4bd',
      custom2: '#397ef0',
      barData: [],
      xAxisData: [],
      xAxisData2: [],
      colorArr: [],
      titleText: '',
      lineData: [],
      xAxisDataLline: [],
      barData2: [],
      colorArr2: [],
      titleText2: '',
      mapData: [],
      tableData: [
        {
          name: '济南',
          address: '466'
        }
      ],
      tableList: {
        pageSize: 5,
        currentPage: 1
      },
      total: 0,
      pickerOptions0: {
        disabledDate(time) {
          return time.getTime() > Date.now()
        }
      }
    }
  },
  created() {
    this.titleText = '数量'
    this.colorArr = [{ top: '#4084ef', bottom: 'rgba(64, 132, 239, 0.1)' }, { top: '#37d6b7', bottom: 'rgba(55, 214, 183, 0.1)' }, { top: '#ff7d70', bottom: 'rgba(255, 125, 112, 0.1)' }, { top: '#ffab32', bottom: 'rgba(255, 171, 50, 0.1)' }, { top: '#4185ef', bottom: 'rgba(65, 133, 239, 0.1)' }, { top: '#38d7b7', bottom: 'rgba(56, 215, 183, 0.1)' }]
    this.colorArr2 = [{ top: '#ffb541', bottom: 'rgba(255, 181, 65, 0.1)' }]
    this.titleText2 = '调用频率（次/min）'
  },
  mounted() {
    this.initData()
  },
  methods: {
    filterArr: function(objList, key, findName) {
      // debugger;
      findName = findName || 'SeriesName'
      return objList.filter(function(item) {
        return key.indexOf(item[findName]) !== -1
      })
    },
    initData() {
      this.getsyqk()
      this.getzydy()
      this.getwtfl()
      this.getwtfliu()
      this.getqxyj()
      this.getMapData()
      this.getTableMap()
    },
    selectDate: function() {
      const dateTime = this.listQuery.fxsj
      if (dateTime == null) {
        this.listQuery.kssj = ''
        this.listQuery.jssj = ''
      } else {
        const startTime = this.moment(dateTime[0]).format('YYYY-MM-DD')
        const endTime = this.moment(dateTime[1]).format('YYYY-MM-DD')
        this.listQuery.kssj = startTime
        this.listQuery.jssj = endTime
      }
    },
    onSearch: function() {
      this.tableList.currentPage = 1
      this.initData()
    },
    restOff: function(listQuery) {
      this.$refs[listQuery].resetFields()
      this.listQuery.slksrq = ''
      this.listQuery.sljsrq = ''
      this.initData()
    },
    getsyqk() {
      this.$api.queryZyksyqk(this.listQuery).then(response => {
        if (response.code === 200) {
          this.barData = []
          this.barData.push(response.data.jdalk)
          this.barData.push(response.data.jdfvfg)
          this.barData.push(response.data.yxztd)
          this.barData.push(response.data.zjjs)
          this.xAxisData = response.data.citylist
        } else {
          this.$message({
            message: response.message,
            type: 'warning'
          })
        }
      })
    },
    getzydy() {
      this.$api.queryZydyfx(this.listQuery).then(response => {
        if (response.code === 200) {
          const list = response.data.result
          this.barData2 = []
          this.barData2.push({ name: '调用频率', data: list })
          this.xAxisData2 = response.data.citylist
        } else {
          this.$message({
            message: response.message,
            type: 'warning'
          })
        }
      })
    },
    getwtfl() {
      this.$api.getWtCount(this.listQuery).then(response => {
        if (response.length > 0) {
          this.pieData = response
        } else {
          this.$message({
            message: '接口报错',
            type: 'warning'
          })
        }
      })
    },
    getwtfliu() {
      this.$api.getFlCount(this.listQuery).then(response => {
        if (response.length > 0) {
          const progressData = response
          this.progressData0 = progressData[0]
          this.progressData1 = progressData[1]
        } else {
          this.$message({
            message: '接口报错',
            type: 'warning'
          })
        }
      })
    },
    getqxyj() {
      this.$api.getQxyjfxCount(this.listQuery).then(response => {
        if (response.barData.length > 0) {
          this.lineData = response.barData
          this.xAxisDataLline = response.dw
        } else {
          this.$message({
            message: '接口报错',
            type: 'warning'
          })
        }
      })
    },
    indexMethod(index) {
      return index
    },
    getMapData() {
      this.$api.getWtflCount(this.listQuery).then(response => {
        if (response.value.length > 0) {
          this.mapData = response.value
        } else {
          this.$message({
            message: '接口报错',
            type: 'warning'
          })
        }
      })
    },
    clickTableMap(item) {
      const params = {
        kssj: this.listQuery.kssj,
        jssj: this.listQuery.jssj,
        pageSize: this.tableList.pageSize,
        currentPage: this.tableList.currentPage,
        dwbm: item.data.code
      }
      this.$api.getWtListCountFl(params).then(response => {
        if (response.code === 200) {
          this.tableData = response.data.list
        } else {
          this.$message({
            message: response.message,
            type: 'warning'
          })
        }
      })
    },
    getTableMap() {
      const params = {
        kssj: this.listQuery.kssj,
        jssj: this.listQuery.jssj,
        pageSize: this.tableList.pageSize,
        currentPage: this.tableList.currentPage,
        dwbm: ''
      }
      this.$api.getWtListCountFl(params).then(response => {
        if (response.code === 200) {
          this.tableData = response.data.list
        } else {
          this.$message({
            message: response.message,
            type: 'warning'
          })
        }
      })
    },
    refresh() {
      this.clickTableMap()
    },
  }
}
