result:
.back{
    color: rgb(74, 139, 199);
    padding: 10px 20px;
}
.neirong{
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
    .top{
        font-weight: 600;
        font-size: 40px;
        margin: 20px;
    }
    .time{
        margin-bottom: 40px;
        .left{
            font: 800;
            color: black;
        }
        .right{
            color: rgb(49, 47, 47);
        }
    }
    .conten{
        text-indent: 30px;
        width: 60%;
        text-align: justify;
        word-wrap: wrap;
        word-break: break-all;
        padding-top: 40px;
    }
    .xinxi{
        margin-bottom: 20px;
    }
    .two-item{
      width: 80%;
      display: flex;
      margin: 20px 0;
       .left{
        width: 70%;
        box-sizing: border-box;
        height: 400px;
        border-bottom: 1px solid #e1e7ef;
        border-top: 1px solid #e1e7ef;
        border-left: 1px solid #e1e7ef;
        padding-top: 16px;
    }
    .right{
        width: 30%;
        box-sizing: border-box;
        height: 400px;
        border-bottom: 1px solid #e1e7ef;
        border-top: 1px solid #e1e7ef;
        border-left: 1px dashed #e1e7ef;
        border-right: 1px dashed #e1e7ef;
        padding-top: 16px;
    }
}
}
