import Pagination from '@/components/Pagination/index'
export default {
  name: 'ReviewArrest',
  components: { Pagination },
  data() {
    return {
      form: {
        title: '',
        start: '',
        end: '',
      },
      listQuery: {
        pageNum: 1,
        pageSize: 20,
        keyWord: ''
      },
      infoPushVisible: false,
      tableHeader: [
        { filed: 'title', title: '标题', colorB: 'colorB', width: '200' },
        { filed: 'updateTime', title: '创建时间' },
      ],
      tableData: [
      ],
      listLoading: true,
      total: 100
    }
  },
  created() {
    this.getTime()
  },
  mounted() {
    this.getList()
  },
  methods: {
    handleFilter() {
      this.$api.getlvshiwrok({ params: {
        title: this.form.title,
        startTime: this.form.start,
        endTime: this.form.end
      }}).then(res => {
        this.tableData = res.data
      })
    },
    getList() {
      this.listLoading = false
    },
    handleClick(row) {
      this.$router.push({
        name: 'gzzdxq',
        params: {
          id: row.id
        }
        
      })
    },
    refresh(val) {
    },
    getTime() {
      const nowDate = new Date()
      const date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      }
      let mouth = ''
      // 月
      if (date.month < 10) {
        mouth = '0' + date.month
      } else {
        mouth = date.month
      }
      // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }
      this.form.start = date.year - 1 + '-' + mouth
      this.form.end = date.year + '-' + mouth
    }
  }
}
