.neirong{
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
    .top{
        font-weight: 1000;
        font-size: 40px;
        margin: 20px;
    }
    .time{
        .left{
            font: 800;
            color: black;
        }
        .right{
            color: rgb(49, 47, 47);
        }
    }
    .conten{
        width: 90%;
        text-align: justify;
        word-wrap: wrap;
        word-break: break-all;
        padding-top: 10px;
        padding-bottom: 10px;
        color: white;
        margin: 20px 0;
        // padding-right: 100px;
        background-color: rgb(85, 134, 173);
    }
    .tubiao{
      width: 86%;
      table {  
        border-top: 1px solid #a9c4df;
        border-left: 1px solid #a9c4df;
        .firstRow{
          td{
         border-bottom: 1px solid #a9c4df;
         border-right: 1px solid #a9c4df;
      }
        }
      }
      
    }
}
.title{
    display: flex;
    justify-content: space-between;
    margin-bottom: 20px;
    margin-top: 20px;
    .wenzi{
        width: 18%;
        padding: 20px 10px;
        background: #ebf3fe;
        border: 1px solid #a9c4df;
        .diyih{
          display: flex;
          justify-content: space-between;
          align-items: center;
          .zongshu{
              padding: 10px 10px;
              font-size: 30px;
              font-weight: 200;
          }
          .zengzhang{
              padding: 10px 10px;
              span{
                font-size: 22px;
                color: red;
              }
          }
        }
        .count-item{
            padding: 0 10px;
        }
    }
}
