export default {
  data() {
    return {
      tableHeader1: [
        { filed: 'visitorName', title: '咨询人姓名', colorB: 'colorB', width: '200' },
        { filed: 'mobile', title: '手机号码' },
        { filed: 'createTime', title: '咨询时间' },
        { filed: 'optionValueName', title: '咨询类型' },
        { filed: 'isReply', title: '是否回复' },
      ],
      tableHeader2: [
        { filed: 'visitorName', title: '咨询人姓名', colorB: 'colorB', width: '200' },
        { filed: 'mobile', title: '手机号码' },
        { filed: 'createTime', title: '咨询时间' },
        { filed: 'optionValueName', title: '咨询类型' },
        { filed: 'state', title: '状态' },
      ],
      tableHeader3: [
        { filed: 'visitorName', title: '咨询人姓名', colorB: 'colorB', width: '200' },
        { filed: 'mobile', title: '手机号码' },
        { filed: 'createTime', title: '咨询时间' },
        { filed: 'optionValueName', title: '咨询类型' },
        { filed: 'state', title: '状态' },
      ],
      tableData1: [],
      tableData2: [],
      tableData3: [],
      listLoading1: false,
      listLoading2: false,
      listLoading3: false,
      tongjishuju: [],
      casecount: [],
      work: [],
      report: [],
    }
  },
  created() {
    this.listLoading1 = true
    this.listLoading2 = true
    this.listLoading3 = true
    this.$api.getTongJi()
      .then(res => {
        console.log(res)
        if (res.code === 0) {
          this.tongjishuju = res.data
        }
      }).catch(err => {
        console.log(err)
      })
    this.$api.getWldaiban()
      .then(res => {
        console.log(res)
        if (res.code === 0) {
          this.listLoading1 = false
          this.listLoading2 = false
          this.listLoading3 = false
          this.tableData1 = res.data.consulting
          this.tableData2 = res.data.interview
          this.tableData3 = res.data.windowsInterview
        }
      })
    this.$api.getHomegonggao()
      .then(res => {
        console.log(res)
        this.work = res.data.work
        this.casecount = res.data.case
        this.report = res.data.report
      })
  },
  mounted() {
  },
  methods: {
    handleClick(row) {
      this.$router.push({
        name: 'wljfxq',
        params: {
          id: row.id
        }
      })
    },
    handleWatch(row) {
      this.$router.push({
        name: 'ckjfxq',
        params: {
          id: row.id,
          state: row.state
        }
      })
    },
    handleback(row) {
      this.$router.push({
        name: 'wlzxxq',
        params: {
          id: row.id,
          length: row.isReply
        }
      })
    },
    gomorework() {
      this.$router.push({
        name: 'gzzd',
      })
    },
    gomoreshuju() {
      this.$router.push({
        name: 'lvsjbg',
      })
    },
    gomorecase() {
      this.$router.push({
        name: 'yxalzs',
      })
    },
    clickone(id) {
      this.$router.push({
        name: 'gzzdxq',
        params: {
          id: id
        }
      })
    },
    clicktwo(id) {
      this.$router.push({
        name: 'lsbgxq',
        params: {
          id: id
        }
      })
    },
    clickthr(id) {
      this.$router.push({
        name: 'yxalzsxq',
        params: {
          id: id
        }
      })
    },
  }
}
