  data() {
    return {
      show: true,
      riqi: '',
      xingqi: '',
      username: '',
      time: '',
      data: [
      ],
      message: {},
      hours: '',
      chatData: '',
    }
  },
  created() {
    this.getTime()
    this.$api.wljfxq(this.$route.params.id).then(res => {
      console.log(res)
      if (res.code === 0) {
        this.message = res.data
      }
    })
    this.$api.wljfchartxq(this.$route.params.id).then(res => {
      console.log(res)
      if (res.code === 0) {
