export default {
  data() {
    return {
      form: {
        time: []
      },
      tableList1: {
        title: {
          text: '网络咨询趋势分析'
        },
        xAxis: {
          type: 'category',
          boundaryGap: false
        },
        yAxis: {
          type: 'value',
          boundaryGap: [0, '30%']
        },
        visualMap: {
          type: 'piecewise',
          show: false,
          dimension: 0,
          seriesIndex: 0,
          pieces: [
            {
              gt: 0,
              lt: 8,
              color: 'rgba(0, 180, 0, 0.5)'
            },
          ]
        },
        series: [
          {
            type: 'line',
            smooth: 0.6,
            symbol: 'none',
            lineStyle: {
              color: 'drakgreen',
              width: 5
            },
            markLine: {
              symbol: ['none', 'none'],
              label: { show: false },
              data: [
                { xAxis: 1 },
                { xAxis: 2 },
                { xAxis: 3 },
                { xAxis: 4 },
                { xAxis: 5 },
                { xAxis: 6 },
                { xAxis: 7 },
                { xAxis: 8 },
                { xAxis: 9 }
              ]
            },
            areaStyle: {},
            data: [
              ['2019-10-10', 200],
              ['2019-10-11', 400],
              ['2019-10-12', 650],
              ['2019-10-13', 500],
              ['2019-10-14', 250],
              ['2019-10-15', 300],
              ['2019-10-16', 450],
              ['2019-10-17', 300],
              ['2019-10-18', 100]
            ]
          }
        ]
      },
      tableList2: {
        title: {
          text: '网络咨询类型分析',
        },
        tooltip: {
          trigger: 'item',
          formatter: '{ a } <br/> {b }:  {c } ({ d }%)'
        },
        legend: {
          bottom: 20,
          left: 'center',
          data: ['民事', '刑事', '行政', '其他']
        },
        series: [
          {
            name: '访问来源',
            type: 'pie',
            radius: '55%',
            center: ['50%', '50%'],
            data: [
              { value: 335, name: '民事' },
              { value: 310, name: '刑事' },
              { value: 234, name: '行政' },
              { value: 135, name: '其他' },
            ],
            emphasis: {
              itemStyle: {
                shadowBlur: 10,
                shadowOffsetX: 0,
                shadowColor: 'rgba(0, 0, 0, 0.5)'
              }
            }
          }
        ]
      },
      tableList3: {
        title: {
          text: '网络咨询状态分析',
        },
        tooltip: {
          trigger: 'item',
          formatter: '{ a } <br/>{ b }: { c } ({ d }%)'
        },
        legend: {
          data: ['待回复', '已回复'],
          bottom: 20,
          left: 'center'
        },
        series: [
          {
            name: '访问来源',
            type: 'pie',
            radius: ['40%', '60%'],
            center: ['50%', '50%'],
            avoidLabelOverlap: false,
            label: {
              show: false,
              position: 'center'
            },
            emphasis: {
              label: {
                show: true,
                fontSize: '30',
                fontWeight: 'bold'
              }
            },
            labelLine: {
              show: false
            },
            data: [
              { value: 335, name: '待回复' },
              { value: 310, name: '已回复' },
            ]
          }
        ]
      },
    }
  },
  computed: {
  },
  created() {
  },
  mounted() {
    this.mainChart()
    this.middChart()
    this.rightChart()
  },
  methods: {
    handleBack() {},
    handleFilter() {
      // 化解图标数据
      this.$api.huajietongji({
        params: {
          procuratorateId: this.form.jianchayuan,
          lawyerId: this.form.lvshi,
          startTime: this.form.time[0],
          endTime: this.form.time[1]
        }
      }).then(res => {
        console.log(res)
      })
      // 窗口图标数据
      this.$api.chuangkoutongji({
        params: {
          procuratorateId: this.form.jianchayuan,
          lawyerId: this.form.lvshi,
          startTime: this.form.time[0],
          endTime: this.form.time[1]
        }
      }).then(res => {
        console.log(res)
      })
      // 网络咨询图标数据
      this.$api.wlzixuntongji({
        params: {
          procuratorateId: this.form.jianchayuan,
          lawyerId: this.form.lvshi,
          startTime: this.form.time[0],
          endTime: this.form.time[1]
        }
      }).then(res => {
        console.log(res)
      })
      // 网络接访图表数据
      this.$api.wljiefangtongji({
        params: {
          procuratorateId: this.form.jianchayuan,
          lawyerId: this.form.lvshi,
          startTime: this.form.time[0],
          endTime: this.form.time[1]
        }
      }).then(res => {
        console.log(res)
      })
      // 顶部数据
      this.$api.dingbuangtongji({
        params: {
          procuratorateId: this.form.jianchayuan,
          lawyerId: this.form.lvshi,
          startTime: this.form.time[0],
          endTime: this.form.time[1]
        }
      }).then(res => {
        console.log(res)
      })
    },
    mainChart() {
      // 基于准备好的dom，初始化echarts实例
      const myChart = this.$echarts.init(document.getElementById('main'))
      // 指定图表的配置项和数据
      const option = this.tableList1
      // 使用刚指定的配置项和数据显示图表。
      myChart.setOption(option)
    },
    middChart() {
      // 基于准备好的dom，初始化echarts实例
      const myChart = this.$echarts.init(document.getElementById('midd'))
      // 指定图表的配置项和数据
      const option = this.tableList2
      // 使用刚指定的配置项和数据显示图表。
      myChart.setOption(option)
    },
    rightChart() {
      // 基于准备好的dom，初始化echarts实例
      const myChart = this.$echarts.init(document.getElementById('right'))
      // 指定图表的配置项和数据
      const option = this.tableList3
      // 使用刚指定的配置项和数据显示图表
      myChart.setOption(option)
    },
  },
}
