import PieCharts from '@/components/Charts/PieCharts'
export default {
  components: { PieCharts },
  data() {
    return {
      form: {
        name: '',
        lanmu: '',
      },
      yulanShow: false,
      pieData: [
        { value: 335, name: '民事' },
        { value: 310, name: '刑事' },
        { value: 234, name: '行政' },
        { value: 135, name: '其他' },
      ],
      time1: ['', ''],
      time2: ['', ''],
      show1: false,
      show2: false,
      message1:[],
      dialogVisible: false,
      dialogVisible1: false,
      msg: '',
      daiding: '',
      myConfig: {
        // 编辑器不自动被内容撑高
        autoHeightEnabled: false,
        // 初始容器高度
        initialFrameHeight: 240,
        // 初始容器宽度
        initialFrameWidth: '100%',
        // serverUrl: 'http://35.201.165.105:8000/controller.php',
        // UEditor 资源文件的存放路径，如果你使用的是 vue-cli 生成的项目，通常不需要设置该选项，vue-ueditor-wrap 会自动处理常见的情况，如果需要特殊配置，参考下方的常见问题2
        // UEDITOR_HOME_URL: '/UEditor/'
      },
      tableList1: {
        title: {
          text: '网络咨询趋势分析'
        },
        xAxis: {
          type: 'category',
          boundaryGap: false
        },
        yAxis: {
          type: 'value',
          boundaryGap: [0, '30%']
        },
        visualMap: {
          type: 'piecewise',
          show: false,
          dimension: 0,
          seriesIndex: 0,
          pieces: [
            {
              gt: 0,
              lt: 8,
              color: 'rgba(0, 180, 0, 0.5)'
            },
          ]
        },
        series: [
          {
            type: 'line',
            smooth: 0.6,
            symbol: 'none',
            lineStyle: {
              color: 'drakgreen',
              width: 5
            },
            markLine: {
              symbol: ['none', 'none'],
              label: { show: false },
              data: [
                { xAxis: 1 },
                { xAxis: 2 },
                { xAxis: 3 },
                { xAxis: 4 },
                { xAxis: 5 },
                { xAxis: 6 },
                { xAxis: 7 },
                { xAxis: 8 },
                { xAxis: 9 }
              ]
            },
            areaStyle: {},
            data: [
              ['2019-10-10', 200],
              ['2019-10-11', 400],
              ['2019-10-12', 650],
              ['2019-10-13', 500],
              ['2019-10-14', 250],
              ['2019-10-15', 300],
              ['2019-10-16', 450],
              ['2019-10-17', 300],
              ['2019-10-18', 100]
            ]
          }
        ]
      },
    }
  },
  created() {
  },
  methods: {
    handleFilter() {
      this.$api.getzhibiao({
        startTime: this.time1[0],
        endTime: this.time2[1],
        lawyerId: '',
        procuratorateId: ''
      }).then(res => {
        this.show1 = true
        this.messageList = res.data
      })
    },
    handClick() {},
    goback() {
      this.$router.go(-1)
    },
    dialog() {
      this.dialogVisible = false
      this.show1 = false
    },
    Visible() {
      this.dialogVisible1 = false
    },
    zhibiao() {
      this.dialogVisible = true
    },
    tubiao() {
      // this.$message.error('图表工具暂不可用')
      // return false
      console.log(this.$echarts.init)
      this.dialogVisible1= true
      this.mainChart()
      //this.$options.methods.mainChart()
      
    },
    yulan() {
      this.yulanShow = true
      this.daiding = this.daiding + this.msg
      this.$message.success('恭喜预览成功, 您可进行保存或发布操作')
    },
    baocun() {
      if (!this.yulanShow) {
        this.$message.error('请预览后再保存')
        return false
      }
      if (this.form.name == '' || this.form.lanmu == '' || this.daiding == '') {
        this.$message.error('标题、栏目、内容不能为空')
        return false
      }
      this.$api.baocun({
        title: this.form.name,
        typeName: this.form.lanmu,
        content: this.daiding
      }).then(res => {
        if (res.code === 0) {
          this.$message.success('保存成功')
          this.$router.push({
            name: 'xxfb'
          })
        }else{
          this.$message.error('保存失败')
        }
      })
    },
    send() {
      if (!this.yulanShow) {
        this.$message.error('请预览后再发布')
        return false
      }
      if (this.form.name == '' || this.form.lanmu == '' || this.daiding == '') {
        this.$message.error('标题、栏目、内容不能为空')
        return false
      }
      this.$api.send({
        title: this.form.name,
        typeName: this.form.lanmu,
        content: this.daiding
      }).then(res => {
        if (res.code === 0) {
          this.$message.success('发布成功')
          this.$router.push({
            name: 'xxfb'
          })
        }else {
          this.$message.error('发布失败')
        } 
      })
    },
    addzhibiao() {
      this.daiding =  this.daiding + this.$refs.zhibiaohtml.innerHTML
    },
    clickOne() {
      this.daiding = this.daiding + this.$refs.imgone.innerHTML
    },
    mainChart() {
     // 基于准备好的dom，初始化echarts实例
     console.log(this.$echarts.init)
      const myChart = this.$echarts.init(document.getElementById('mainitem'))
      // 指定图表的配置项和数据
      const option = this.tableList1
      // 使用刚指定的配置项和数据显示图表。
      myChart.setOption(option)
    },
  },
  mounted() {
  
    console.log(1111111111111)
    // this.rightChart()
  },
}
