export default {
  data() {
    return {
      biaogeshuju: {},
      show: false,
      show1: false,
      show2: false,
      dialogVisible: false,
      showshow: false,
    }
  },
  created() {
    if (this.$route.params.state == "已过期") {
      this.showshow = true
    }
    if (this.$route.params.state == '已接访') {
      this.show2 = true
      this.show = true
    }
    if (this.$route.params.state == '待接访') {
      this.show1 = true
    }
    this.$api.getchuangkoubiaoge(this.$route.params.id).then(res => {
      console.log(res)
      if (res.code === 0) {
        this.biaogeshuju = res.data
      }else {
        this.$message.error('获取表格数据失败')
      }
    })
  },
  mounted() {},
  methods: {
    beginJf() {
      this.dialogVisible = true
    },
    jiefang() {
      this.$api.getchuangkouchange(this.$route.params.id).then(res => {
        if (res.code === 0) {
          this.show = true
          this.show1 = false
          this.show2 = true
          this.dialogVisible = false
          this.$message.success('接访成功')
        }else {
          this.$message.error('接访失败')
        }
      }).catch(err => {
        console.log(err)
      })
    }
  }
}
