    getTime() {
      const nowDate = new Date()
      const date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1,
        date: nowDate.getDate()
      }
      let mouth = ''
      let day
      // 月
      if (date.month < 10) {
        mouth = '0' + date.month
      } else {
        mouth = date.month
      }
      // 日
      if (date.date < 10) {
          day = '0' + date.date
      } else {
          day = date.date
      }
      this.form.time[0] = date.year - 1 + '-' + mouth + '-' + day
