import Pagination from '@/components/Pagination/index'
export default {
  name: 'ReviewArrest',
  components: { Pagination },
  data() {
    return {
      infoPushVisible: false,
      form: {
        jianchayuan: '',
        leixing: '',
        time: [],
        name: '',
      },
      // tableData: [],
      listQuery: {
        page: 1,
        pageSize: 10,
        keyWord: ''
      },
      tableHeader: [
        { filed: 'lawyerName', title: '律师姓名', colorB: 'colorB', width: '200' },
        { filed: 'visitorMobile', title: '手机号码' },
        { filed: 'appointmentTime', title: '接访时间' },
        { filed: 'visitorName', title: '信访人姓名' },
        { filed: 'typeOptionName', title: '信访类型' },
        { filed: 'fromProcuratorateName', title: '共享单位名称' },
        { filed: 'targetProcuratorateName', title: '接收单位名称' },
      ],
      tableData: [
      ],
      listLoading: false,
      total: 0
    }
  },
  created() {
    // this.getTime()
  },
  mounted() {
    this.getList()
  },
  methods: {
    handleFilter() {
      this.listLoading = true
      this.listQuery.pageSize = 10
      this.listQuery.page = 1
      this.$api.Rzwachuangkou({
        lawyerName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leixing,
        fromProcuratorateId: this.form.jianchayuan,
        pageNum: 1,
        pageSize: 10,
    }).then(res => {
        console.log(res)
        if (res.code === 0) {
          if (res.data) {
            this.tableData = res.data.pageList
            this.total = res.data.total
            this.listLoading = false
          }else {
            this.$message.warning('暂无数据哦')
            this.tableData = []
            this.listLoading = false
          }
        }  
      })
    },
    getList() {
      this.listLoading = false
    },
    chongzhi() {
      this.form.name = ''
      this.form.jianchayuan = ''
      this.form.leixing = ''
      this.form.time = ['', '']
    },
    // handleBack(row) {
    //   this.$confirm('你是否确认接收选中的档案?', '提示', {
    //     confirmButtonText: '确定',
    //     cancelButtonText: '取消',
    //     customClass: 'message-manage',
    //     type: 'warning'
    //   }).then(() => {
    //     this.$api.jieshouwljiefang(row.id).then(res => {
    //     console.log(res)
    //     if (res.code === 0) {
    //       this.$message.success('接收成功')
    //     }else {
    //       this.$message.error('接收失败')
    //     }  
    //   })
    //   }) 
    // },
    handleClick(row) {
      this.$router.push({
        name: 'jcwljfxq',
        params: {
          id: row.interviewId
        }
      })
    },
    refresh(val) {
      this.listLoading = true
      this.$api.Rzwachuangkou({
        lawyerName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leixing,
        fromProcuratorateId: this.form.jianchayuan,
        pageNum: val.page,
        pageSize: val.limit,
    }).then(res => {
        console.log(res)
        if (res.code === 0) {
          if (res.data) {
            this.tableData = res.data.pageList
            this.total = res.data.total
            this.listLoading = false
          }else {
            this.$message.warning('暂无数据哦')
            thia.tableData = []
            this.listLoading = false
          }
        }  
      })
    },
    getTime() {
      const nowDate = new Date()
      const date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      }
      let mouth = ''
      // 月
      if (date.month < 10) {
        mouth = '0' + date.month
      } else {
        mouth = date.month
      }
      // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }
      this.form.start = date.year - 1 + '-' + mouth
      this.form.end = date.year + '-' + mouth
    }
  }
}
