import Pagination from '@/components/Pagination/index'
export default {
  name: 'ReviewArrest',
  components: { Pagination },
  data() {
    return {
      form: {
        leibie: '',
        state: '',
        name: '',
        start: '',
        end: '',
      },
      // tableData: [],
      listQuery: {
        pageNum: 1,
        pageSize: 10,
        keyWord: ''
      },
      tableHeader: [
        { filed: 'visitorName', title: '咨询人姓名', colorB: 'colorB', width: '200' },
        { filed: 'mobile', title: '手机号码' },
        { filed: 'createTime', title: '咨询时间' },
        { filed: 'optionValueName', title: '咨询类型' },
        { filed: 'isReply', title: '状态' },
      ],
      tableData: [
      ],
      listLoading: true,
      total: 100
    }
  },
  created() {
    this.getTime()
  },
  mounted() {
    this.getList()
  },
  methods: {
    handleFilter() {
      for (var k in this.form) {
        if (this.form[k] == '') {
          this.$message.error('请输入完整信息后在查询')
          return false
        }
      }
      this.$api.getWlzixunList({
        visitorName: this.form.name,
        startTime: this.form.start,
        endTime: this.form.end,
        optionValue: this.form.leibie,
        state: this.form.state,
        pageNum: 1,
        pageSize: 10,
      }).then(res => {
        this.tableData = res. data
      }).catch(err => {
        console.log(err)
      })
    },
    getList() {
      this.listLoading = false
    },
    handlewatch(row) {
      this.$router.push({
        name: 'wlzxxq',
        params: {
          id: row.id,
          length: row.isReply.length
        }
      })
    },
    refresh(val) {
    },
    getTime() {
      const nowDate = new Date()
      const date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      }
      let mouth = ''
      // 月
      if (date.month < 10) {
        mouth = '0' + date.month
      } else {
        mouth = date.month
      }
      // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }
      this.form.start = date.year - 1 + '-' + mouth
      this.form.end = date.year + '-' + mouth
    }
  }
}
