@import '~@/styles/mixin.scss';
@import '~@/styles/variables.scss';

.app-wrapper {
  @include clearfix;
  position: relative;
  height: 100%;
  width: 100%;
  background-color: #ecf1fa;
  .app-header {
    height: 49px !important;
    background-color: #2269d6;
    color: #fefefe;
    line-height: 49px;
    font-size: 18px;
    padding: 0 10px !important;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    .app-logOut {
      position: absolute;
      right: 15px;
      top: 0;
      font-size: 16px;
      color: #fff;
      cursor: pointer;
      img {
        margin-top: 16px;
        margin-right: 5px;
        float: left;
      }
    }
    .topLogo {
      float: left;
      img {
        margin-top: 13px;
        margin-right: 5px;
      }
    }
    .sa-message {
      margin-left: 20px;
      padding-left: 20px;
      border-left: 1px solid #6d9ce4;
      font-size: 16px;
      cursor: pointer;
      i {
        font-size: 18px;
        vertical-align: middle;
      }
    }
  }
  &.mobile.openSidebar {
    position: fixed;
    top: 0;
  }
}

.drawer-bg {
  background: #000;
  opacity: 0.3;
  width: 100%;
  top: 0;
  height: 100%;
  position: absolute;
  z-index: 999;
}

.fixed-header {
  position: fixed;
  top: 0;
  right: 0;
  z-index: 9;
  width: calc(100% - #{$sideBarWidth});
  transition: width 0.28s;
}

.hideSidebar .fixed-header {
  width: calc(100% - 54px);
}

.mobile .fixed-header {
  width: 100%;
}
