export default {
  name: 'ReviewArrest',
  data() {
    return {
      message: {},
      tableList1: {
        title: {
          text: '网络咨询趋势分析'
        },
        xAxis: {
          type: 'category',
          boundaryGap: false
        },
        yAxis: {
          type: 'value',
          boundaryGap: [0, '30%']
        },
        visualMap: {
          type: 'piecewise',
          show: false,
          dimension: 0,
          seriesIndex: 0,
          pieces: [
            {
              gt: 0,
              lt: 8,
              color: 'rgba(0, 180, 0, 0.5)'
            },
          ]
        },
        series: [
          {
            type: 'line',
            smooth: 0.6,
            symbol: 'none',
            lineStyle: {
              color: 'drakgreen',
              width: 5
            },
            markLine: {
              symbol: ['none', 'none'],
              label: { show: false },
              data: [
                { xAxis: 1 },
                { xAxis: 2 },
                { xAxis: 3 },
                { xAxis: 4 },
                { xAxis: 5 },
                { xAxis: 6 },
                { xAxis: 7 },
                { xAxis: 8 },
                { xAxis: 9 }
              ]
            },
            areaStyle: {},
            data: [
              ['2019-10-10', 200],
              ['2019-10-11', 400],
              ['2019-10-12', 650],
              ['2019-10-13', 500],
              ['2019-10-14', 250],
              ['2019-10-15', 300],
              ['2019-10-16', 450],
              ['2019-10-17', 300],
              ['2019-10-18', 100]
            ]
          }
        ]
      },
      tableList2: {
        title: {
          text: '网络咨询类型分析',
        },
        tooltip: {
          trigger: 'item',
          formatter: '{ a } <br/> {b }:  {c } ({ d }%)'
        },
        legend: {
          bottom: 20,
          left: 'center',
          data: ['民事', '刑事', '行政', '其他']
        },
        series: [
          {
            name: '访问来源',
            type: 'pie',
            radius: '55%',
            center: ['50%', '50%'],
            data: [
              { value: 335, name: '民事' },
              { value: 310, name: '刑事' },
              { value: 234, name: '行政' },
              { value: 135, name: '其他' },
            ],
            emphasis: {
              itemStyle: {
                shadowBlur: 10,
                shadowOffsetX: 0,
                shadowColor: 'rgba(0, 0, 0, 0.5)'
              }
            }
          }
        ]
      },
    }
  },
  created() {
    this.$api.chakan(this.$route.params.id).then(res => {
      console.log(res)
      this.message = res.data
    })
  },
  mounted() {
    this.mainChart()
    this.middChart()
  },
  methods: {
    refresh(val) {
    },
    mainChart() {
      // 基于准备好的dom，初始化echarts实例
      const myChart = this.$echarts.init(document.getElementById('main'))
      // 指定图表的配置项和数据
      const option = this.tableList1
      // 使用刚指定的配置项和数据显示图表。
      myChart.setOption(option)
    },
    middChart() {
      // 基于准备好的dom，初始化echarts实例
      const myChart = this.$echarts.init(document.getElementById('midd'))
      // 指定图表的配置项和数据
      const option = this.tableList2
      // 使用刚指定的配置项和数据显示图表。
      myChart.setOption(option)
    },
    goback() {
      this.$router.go(-1)
    },
    getTime() {
      const nowDate = new Date()
      const date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      }
      let mouth = ''
      // 月
      if (date.month < 10) {
        mouth = '0' + date.month
      } else {
        mouth = date.month
      }
      // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }
      this.form.start = date.year - 1 + '-' + mouth
      this.form.end = date.year + '-' + mouth
    }
  }
}
