.top{
    display: flex;
    justify-content: space-between;
    height: 800px;
    .left{
        width: 55%;
        height: 100%;
        border: 1px solid #a9c4df;
        .title-one{
            width: 100%;
            padding: 10px;
            color: #5c79b0;
            background: #d7e5f9;
            border: 1px solid #a9c4df;
        }
        .top-item{
              margin: 20px 20px;
              display: flex;
              align-items: center;
              .title-ite{
                  display: inline-block;
                  width: 4px;
                  height: 16px;
                  background: cornflowerblue;
              }
              .title-item{
                  padding-left: 5px;
                  display: inline-block;
                  color: black;
                  font-weight: 800;
              }
        }
        .table-item{
              width: 90%;
              margin: 20px 20px;
              position: relative;
              img{
                  position: absolute;
                  right: -50px;
                  top: -50px;
              }
              .contenitem{
                  color:#4882bb ;
              }
              .titleitem{
                  color: #717375;
              }
              table{
                  border:#a9c4df;
                  tr td{
                      text-align: center;
                  }
              }
        }
        .open{
            width: 40%;
            margin: 100px auto;
            text-align: center;
            .jiefang{
                color: white;
                background: #1b9dff;
                padding: 10px 30px;
                border-radius: 6px;
            }
        }
    }
    .right{
        width: 43%;
        height: 100%;
        border: 1px solid #a9c4df;
        .title-one{
            width: 100%;
            padding: 10px;
            color: #5c79b0;
            background: #d7e5f9;
            border: 1px solid #a9c4df;
        }
         .jiaoliu{
              width: 80%;
              margin: 20px auto;
              background-color: #f4f4f4;
              height: 600px;
              position: relative;
              .kuang{
                  width: 90%;
                  margin: 20px auto;
                  background: #f4f4f4;
                  height: 300px;
                  list-style: none;
                  li{
                      list-style: none;
                      margin-bottom: 20px;
                      .name{
                          padding-right: 10px;
                          padding-bottom: 4px;
                          font-weight: 1000;
                      }
                      .time{
                          padding-bottom: 4px;
                          color: rgb(116, 116, 116);
                      }
                      .neirong{
                          padding-left: 20px;
                          word-wrap: break-word;
                          word-break: normal;
                          color: #4882d1;
                          text-indent:20px;
                          padding-top: 6px;
                      }
                  }
              }
              .message-item{
                  width: 90%;
                  margin: 0 auto;
                  input{
                      width: 100%;
                      border: 1px solid #d4c6c6;
                      outline: none;
                      padding-bottom: 150px;
                      text-indent:20px;
                  }
              }
              .but{
                  padding: 10px 20px;
                  color: white;
                  width: 80px;
                  background-color:rgb(79, 146, 223) ;
                  text-align: center;
                  border-radius: 6px;
                  float: right;
                  margin-top: 30px;
                  margin-right: 5%;
              }
          }
    }
}
