import Pagination from '@/components/Pagination/index'
export default {
  name: 'ReviewArrest',
  components: { Pagination },
  data() {
    return {
      anjianinfo: [],
      infoPushVisible: false,
      bianGenInfo: {
        czrymc: '',
        bgrq: '',
        optContent: '',
      },
      form: {
        leibie: '',
        time: ['', ''],
        name: '',
      },
      // tableData: [],
      listQuery: {
        page: 1,
        pageSize: 10,
        keyWord: ''
      },
      tableHeader: [
        { filed: 'objectName', title: '咨询人姓名', colorB: 'colorB', width: '200' },
        { filed: 'phone', title: '手机号码' },
        { filed: 'handleTime', title: '处理时间' },
        { filed: 'optionValueName', title: '咨询类型' },
      ],
      tableData: [
      ],
      listLoading: false,
      total: 0
    }
  },
  created() {
    // this.getTime()
  },
  mounted() {
    // this.getList()
  },
  methods: {
    handleFilter() {
      this.listLoading = true
      this.listQuery.page = 1
      this.listQuery.pageSize = 10
      this.$api.getwlhuajie({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leibie,
        pageNum: 1,
        pageSize: 10,
      }).then(res => {
        console.log(res)
        if (res.code === 0) {
          if (res.data) {
            this.tableData = res.data.pageList
            this.total = res.data.total
            this.listLoading = false
          }else {
            this.$message.warning('暂无数据哦')
            this.listLoading = false
            this.tableData = []
          }
        }
      }).catch(err => {
        console.log(err)
      })
    },
    chongzhi() {
      this.form.time = ['', '']
      this.form.name = ''
      this.form.leibie = ''
    },
    handleClick(row) {
      this.$router.push({
        name: 'hjxq',
        params: {
          id: row.id
        }
      })
    },
    handleChange(row) {},
    handleDle(row) {},
    handleAdd() {
      this.$router.push({
        name: 'tjdj',
      })
    },
    getList() {
      this.listLoading = false
    },
    refresh(val) {
      this.listLoading = true
      this.$api.getwlhuajie({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leibie,
        pageNum: val.page,
        pageSize: val.limit,
      }).then(res => {
        console.log(res)
        if (res.code === 0) {
          if (res.data) {
            this.tableData = res.data.pageList
            this.total = res.data.total
            this.listLoading = false
          }else {
            this.$message.warning('暂无数据哦')
            this.listLoading = false
            this.tableData = []
          }
        }
      }).catch(err => {
        console.log(err)
      })
    },
    getTime() {
      const nowDate = new Date()
      const date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      }
      let mouth = ''
      // 月
      if (date.month < 10) {
        mouth = '0' + date.month
      } else {
        mouth = date.month
      }
      // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }
      this.form.start = date.year - 1 + '-' + mouth
      this.form.end = date.year + '-' + mouth
    }
  }
}
