export default {
  data() {
    return {
      form: {
        startDate: '',
        endDate: '',
      },
      tableList: {
        title: {
          text: '远程指挥次数趋势分析'
        },
        xAxis: {
          type: 'category',
          data: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Now', 'Dec']
        },
        yAxis: {
          type: 'value'
        },
        series: [{
          data: [820, 932, 901, 934, 1290, 1330, 1320, 1204, 1110, 1203, 1204, 1045],
          type: 'line'
        }],
      },
      dataList: {
        title: {
          text: '涉法涉诉案件登记趋势分析'
        },
        xAxis: {
          type: 'category',
          data: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Now', 'Dec']
        },
        yAxis: {
          type: 'value'
        },
        series: [{
          data: [820, 932, 901, 934, 1290, 1330, 1320, 1204, 1110, 1203, 1204, 1045],
          type: 'line'
        }],
      },
      leftList: {
        title: {
          text: '案件办理情况分析'
        },
        tooltip: {
          trigger: 'item',
        },
        legend: {
          orient: 'vertical',
          left: 10,
          top: 50,
          data: ['已办结', '带办理']
        },
        series: [
          {
            name: '案件办理情况分析',
            type: 'pie',
            radius: ['50%', '70%'],
            avoidLabelOverlap: false,
            label: {
              show: false,
              position: 'center'
            },
            emphasis: {
              label: {
                show: true,
                fontSize: '30',
                fontWeight: 'bold'
              }
            },
            labelLine: {
              show: false
            },
            data: [
              { value: 335, name: '已办结' },
              { value: 310, name: '带办理' },
            ]
          }
        ]
      },
      rightList: {
        title: {
          text: '信访案件类型分析'
        },
        tooltip: {
          trigger: 'item',
        },
        legend: {
          orient: 'vertical',
          left: 10,
          top: 50,
          data: ['控告', '刑事申诉', '民事申诉', '行政申诉']
        },
        series: [
          {
            name: '案件办理情况分析',
            type: 'pie',
            radius: ['50%', '70%'],
            avoidLabelOverlap: false,
            label: {
              show: false,
              position: 'center'
            },
            emphasis: {
              label: {
                show: true,
                fontSize: '30',
                fontWeight: 'bold'
              }
            },
            labelLine: {
              show: false
            },
            data: [
              { value: 335, name: '控告' },
              { value: 310, name: '刑事申诉' },
              { value: 335, name: '民事申诉' },
              { value: 310, name: '行政申诉' },
            ]
          }
        ]
      },
      lastList: {
        title: {
          text: '远程指挥审核结果分析'
        },
        tooltip: {
          trigger: 'item',
        },
        legend: {
          orient: 'vertical',
          left: 10,
          top: 50,
          data: ['通过', '拒绝']
        },
        series: [
          {
            name: '案件办理情况分析',
            type: 'pie',
            radius: ['50%', '70%'],
            avoidLabelOverlap: false,
            label: {
              show: false,
              position: 'center'
            },
            emphasis: {
              label: {
                show: true,
                fontSize: '30',
                fontWeight: 'bold'
              }
            },
            labelLine: {
              show: false
            },
            data: [
              { value: 335, name: '通过' },
              { value: 310, name: '拒绝' },
            ]
          }
        ]
      },
    }
  },
  computed: {
  },
  mounted() {
    this.drawChart()
    this.octChart()
    this.leftChart()
    this.rightChart()
    this.lastChart()
  },
  methods: {
    clickme(res) {
    },
    drawChart() {
      // 基于准备好的dom，初始化echarts实例
      const myChart = this.$echarts.init(document.getElementById('main'))
      // 指定图表的配置项和数据
      const option = this.tableList
      // 使用刚指定的配置项和数据显示图表。
      myChart.setOption(option)
    },
    octChart() {
      // 基于准备好的dom，初始化echarts实例
      const myChart = this.$echarts.init(document.getElementById('list'))
      // 指定图表的配置项和数据
      const option = this.dataList
      // 使用刚指定的配置项和数据显示图表。
      myChart.setOption(option)
    },
    leftChart() {
      // 基于准备好的dom，初始化echarts实例
      const myChart = this.$echarts.init(document.getElementById('left'))
      // 指定图表的配置项和数据
      const option = this.leftList
      // 使用刚指定的配置项和数据显示图表。
      myChart.setOption(option)
    },
    rightChart() {
      // 基于准备好的dom，初始化echarts实例
      const myChart = this.$echarts.init(document.getElementById('right'))
      // 指定图表的配置项和数据
      const option = this.rightList
      // 使用刚指定的配置项和数据显示图表。
      myChart.setOption(option)
    },
    lastChart() {
      // 基于准备好的dom，初始化echarts实例
      const myChart = this.$echarts.init(document.getElementById('bottom'))
      // 指定图表的配置项和数据
      const option = this.lastList
      // 使用刚指定的配置项和数据显示图表。
      myChart.setOption(option)
    },
  },
}
