result:
$bg: #2d3a4b;
$dark_gray: #889aa4;
$light_gray: #eee;
.el-form-item--medium .el-form-item__content{
    line-height: 36px;
}
.el-input--medium.el-input--suffix{
    width: 95%;
}
.login-container {
  min-height: 100%;
  width: 100%;
  //background-color: $bg;
  background: url("../../icons/img/logoBtn.png") no-repeat;
  background-size: 100%;
  overflow: hidden;

  .login-form {
    position: relative;
    width: 830px;
    height: 532px;
    max-width: 100%;
    padding:15px;
    margin: 0 auto;
    margin-top: 100px;
    background: url("../../icons/img/logoDiv.png") no-repeat;
    background-size: 100%;
    overflow: hidden;
      .login-left{
          width: 348px;
          position: absolute;
          padding-left: 10px;
          margin-top: 8px;
         img{
             width: 100%;
             height: 100%;
         }
      }
      .login-formBox{
          margin-left: 385px;
          width: 378px;
      }
  }

  .tips {
    font-size: 14px;
    color: #fff;
    margin-bottom: 10px;

    span {
      &:first-of-type {
        margin-right: 16px;
      }
    }
  }

  .svg-container {
    padding:0 8px;
    color: $dark_gray;
    vertical-align: middle;
    width: 30px;
    display: inline-block;
    float: left;
    margin-top: 5px;
  }

  .title-container {
    position: relative;

    .title {
      font-size: 26px;
      color: $light_gray;
      margin: 0px auto 40px auto;
      text-align: center;
      font-weight: bold;
      height: 66px;
      margin-top: 55px;
        img{
            height: 100%;
        }
    }
  }

  .show-pwd {
    position: absolute;
    right: 10px;
    top: 7px;
    font-size: 16px;
    color: $dark_gray;
    cursor: pointer;
    user-select: none;
  }

  .thirdparty-button {
    position: absolute;
    right: 0;
    bottom: 6px;
  }

  @media only screen and (max-width: 470px) {
    .thirdparty-button {
      display: none;
    }
  }
}
