import Pagination from '@/components/Pagination/index'
export default {
  name: 'ReviewArrest',
  components: { Pagination },
  data() {
    return {
      anjianinfo: [],
      caseVisible: false,
      isShowClose: false,
      caseShow: false,
      infoPushVisible: false,
      xingfangId: '',
      caseTitle: '共享',
      checdList: [],
      gongXiangListName: [],
      value: [],
      options: [],
      props:{
        multiple: true,
        checkStrictly: true ,
        emitPath: false,
        label: 'name',
        value: 'id',
        children: 'children'
      },
      bianGenInfo: {
        czrymc: '',
        bgrq: '',
        optContent: '',
      },
      form: {
        leixing: '',
        state: '',
        time: ['', ''],
        end: '',
        name: '',
      },
      // tableData: [],
      listQuery: {
        page: 1,
        pageSize: 10,
        keyWord: ''
      },
      tableHeader: [
        { filed: 'lawyerName', title: '律师姓名', colorB: 'colorB', width: '200' },
        { filed: 'mobile', title: '手机号码' },
        { filed: 'createTime', title: '咨询时间' },
        { filed: 'visitorName', title: '咨询人姓名' },
        { filed: 'optionValueName', title: '咨询类型' },
        { filed: 'procuratorateName', title: '信息来源' },
      ],
      tableData: [
      ],
      listLoading: false,
      total: 0
    }
  },
  created() {
    // this.getTime()
    this.$api.getjianchanyuanLIst().then(res => {
      if (res.code === 0) {
        this.options = res.data
      }
    })
  },
  mounted() {
    this.getList()
  },
  watch: {
    value(newValue, oldValue) {
      console.log(newValue)
      this.checdList = []
      this.nameList = []
      let that = this
      for (var i = 0; i < newValue.length; i++) {
        var id = newValue[i]
        this.options.find(item => {
          if (item.id == id) {
            that.$api.getjianchanyuanChildren(item.id).then(res => {
              if (res.code === 0) {
                that.checdList.push(item)
                that.$set(item, 'children', res.data)
                console.log(that.checdList)
              }
            })
          }else {
            if (item.children) {
              that.$options.methods.finditem(item.children,id,that)
            }
          }
        })
      }
    },
  },
  methods: {
    finditem(list,id,that) {
      list.find(item => {
        if (item.id == id) {
          that.checdList.push(item)
          if (item.hasChildren == 1) {
            that.$api.getjianchanyuanChildren(item.id).then(res => {
              if (res.code === 0) {
                that.$set(item, 'children', res.data)
                console.log(that.checdList)
              }
            })
          }  
        }else {
          if (item.children) {
            that.$options.methods.finditem(item.children,id,that)
          }
        }
      })
    },
    // 搜索网络咨询接口
    handleFilter() {
      this.listQuery.page = 1
      this.listQuery.pageSize = 10
      this.listLoading = true
      this.$api.sousuowlzixun({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leixing,
        state: this.form.state,
        pageNum: 1,
        pageSize: 10,
      }).then(res => {
        console.log(res)
        if (res.code === 0) {
          if (res.data) {
            this.tableData = res.data.pageList
            this.total = res.data.total
            this.listLoading = false
          }else {
            this.$message.warning('暂无数据哦')
            this.tableData = []
            this.listLoading = false
          }
        }else {
          this.$message.error('获取数据失败')
          this.listLoading = false
        }
      })
    },
    getList() {
      this.listLoading = false
    },
    chongzhi() {
      this.form.name = ''
      this.form.state = ''
      this.form.leixing = ''
      this.form.time = ['', '']
    },
    displayPop() {
      this.caseVisible = false
    },
    gongxiang() {
      const list = []
      list.push(this.xingfangId)
      this.$api.gongxiangwlzixun({
        list: list,
        procuratorateList: this.value
      }).then(res => {
        if (res.code === 0) {
          this.$message.success('共享成功')
          this.caseVisible = false
          this.value = []
          this.checdList = []
        }else {
          this.$message.error('共享失败')
        }
      })

    },
    handleClick(row) {
      this.$router.push({
        name: 'jcwlzxxq',
        params: {
          id: row.id
        }
      })
    },
    handleSee(row) {
      this.xingfangId = row.id
      this.caseVisible = true
      this.caseShow = true
    },
    refresh(val) {
      this.listLoading = true
      this.$api.sousuowlzixun({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leixing,
        state: this.form.state,
        pageNum: val.page,
        pageSize: val.limit,
      }).then(res => {
        console.log(res)
        if (res.code === 0) {
          if (res.data) {
            this.tableData = res.data.pageList
            this.total = res.data.total
            this.listLoading = false
          }else {
            this.$message.warning('暂无数据哦')
            this.tableData = []
            this.listLoading = false
          }
        }else {
          this.$message.error('获取数据失败')
          this.listLoading = false
        }
      })
    },
    getTime() {
      const nowDate = new Date()
      const date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      }
      let mouth = ''
      // 月
      if (date.month < 10) {
        mouth = '0' + date.month
      } else {
        mouth = date.month
      }
      // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }
      this.form.start = date.year - 1 + '-' + mouth
      this.form.end = date.year + '-' + mouth
    }
  }
}
