import Pagination from '@/components/Pagination/index'
export default {
  name: 'ReviewArrest',
  components: { Pagination },
  data() {
    return {
      anjianinfo: [],
      infoPushVisible: false,
      bianGenInfo: {
        czrymc: '',
        bgrq: '',
        optContent: '',
      },
      form: {
        state: '',
        start: '',
        end: '',
        name: '',
      },
      // tableData: [],
      listQuery: {
        pageNum: 1,
        pageSize: 20,
        keyWord: ''
      },
      tableHeader: [
        { filed: 'title', title: '标题', colorB: 'colorB', width: '200' },
        { filed: 'updateTime', title: '咨询时间' },
        { filed: 'typeName', title: '栏目' },
        { filed: 'state', title: '状态' },
      ],
      tableData: [
      ],
      listLoading: false,
      total: 100
    }
  },
  created() {
    this.getTime()
  },
  mounted() {
    this.getList()
  },
  methods: {
    handleFilter() {
      this.$api.getgonggao({
        params: {
          title: this.form.name,
          startTime: this.form.start,
          endTime: this.form.end,
          state: this.form.state,
          pageNum: 1,
          pageSize: 10,
        }
      }).then(res => {
        this.tableData = res.data
      })
    },
    handleAdd() {
      this.$router.push({
        name: 'xxtj'
      })
    },
    getList() {
      this.listLoading = false
    },
    handleSee(row) {
      this.$router.push({
        name: 'chakan',
        params: {
          id: row.id
        }
      })
    },
    handleChange(row) {
      this.$router.push({
        name: 'xiugai',
        params: {
          id: row.id
        }
      })
    },
    handleDel(row) {
      this.$api.chanchu(row.id).then(res => {
        this.$message.success('删除成功')
      })
    },
    handleSend() {},
    refresh(val) {
    },
    getTime() {
      const nowDate = new Date()
      const date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      }
      let mouth = ''
      // 月
      if (date.month < 10) {
        mouth = '0' + date.month
      } else {
        mouth = date.month
      }
      // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }
      this.form.start = date.year - 1 + '-' + mouth
      this.form.end = date.year + '-' + mouth
    }
  }
}
