export default { 
  data(){
    return{
      show: false,
      show1: false,
      show2: false,
      show3: false,
      show4: false,
      str: '',
      riqi:'',
      xingqi: '',
      username: '',
      time: '',
      biaogeshuju: {},
      data:[
      ],
      hours:'',
      chatData:'',
    }
  },
  methods:{
    sendinfo() {
        const nowDate = new Date()
        const date = {
          hours:nowDate.getHours(),
          minu:nowDate.getMinutes(),
          second:nowDate.getSeconds()
      }
       let hours=''
       let minu=''
       let second=''
       //时
      if (date.hours < 10) {
          hours = '0' + date.hours
      } else {
          hours = date.hours
      }
       //分
      if (date.minu < 10) {
          minu = '0' + date.minu
      } else {
          minu = date.minu
      }
       //秒
      if (date.second < 10) {
          second = '0' + date.second
      } else {
          second = date.second
      }
      let alltime = hours+':'+minu+':'+second
      let message = {}
      message.lawyerName = '小沫沫'
      message.createTime = alltime
      message.content = this.chatData
      this.$api.getWljiefangsendinfo( this.$route.params.id, {
        content: this.chatData
      }).then(res => {
        if (res.code === 0) {
          this.$message.success('回复成功')
          this.chatData = ''
          this.data.push(message)
        }
      })
    },
    beginJf() {
      this.$api.getWljiefangchange(this.$route.params.id).then(res => {
        if (res.code === 0) {
          this.show1 = true
          this.show4 = true
          this.show = false
        }
      })
    },
    endJf() {
      this.$api.getWljiefangchange(this.$route.params.id).then(res =>{
        if (res.code === 0) {
          this.show1 = false
          this.show4 = false
          this.show = false
          this.show2 = true
        }
      })
    },
  },
  created(){
    //  获取月份 星期
    this.$api.getWljiefangxq(this.$route.params.id).then(res => {
      console.log(res)
      if (res.code === 0) {
        this.biaogeshuju = res.data
        this.str = res.data.state
      }
      // 后台返回直接4种中文字符串 直接===判断四种
      if (this.str === '已过期') {
        this.show3 = true
      }
      if (this.str == '已接访') {
        this.show1 = true
        this.show4 = true
      }
      if (this.str == '待接访') {
        this.show1 = true
      }
      if (this.str == '已结束') {
        this.show2 = true
      }
    })
    this.$api.getWljiefangliaotian(this.$route.params.id).then(res => {
      console.log(res)
      if (res.code === 0) {
        this.data = res.data
      } 
    })
  }, 
  mounted() {
  } 
}
