import Pagination from '@/components/Pagination/index'
export default {
  name: 'ReviewArrest',
  components: { Pagination },
  data() {
    return {
      infoPushVisible: false,
      listLoading: false,
      form: {
        leibie: '',
        state: '',
        name: '',
        time: ['', ''],
      },
      // tableData: [],
      listQuery: {
        page: 1,
        pageSize: 10,
        keyWord: ''
      },
      tableHeader: [
        { filed: 'visitorName', title: '咨询人姓名', colorB: 'colorB', width: '200' },
        { filed: 'mobile', title: '手机号码' },
        { filed: 'appointmentTime', title: '咨询时间' },
        { filed: 'optionValueName', title: '咨询类型' },
        { filed: 'state', title: '状态' },
      ],
      tableData: [
      ],
      total: 0
    }
  },
  created() {
    // this.getTime()
  },
  mounted() {
    this.listLoading = true
    this.$api.getchuangkouList({
      visitorName: this.form.name,
      startTime: this.form.time[0],
      endTime: this.form.time[1],
      optionValue: this.form.leibie,
      state: this.form.state,
      pageNum: 1,
      pageSize: 10,
    })
      .then(res => {
        console.log(res)
        if (res.code === 0) {
          if (res.data) {
            this.tableData = res.data.pageList
            this.total = res.data.total
            this.listLoading = false
          }else {
            this.$message.warning('暂无数据哦')
            this.listLoading = false
            this.tableData = []
          }
        }else {
          this.$message.error('获取列表失败')
        }
      }).catch(err => {
        console.log(err)
      })
  },
  methods: {
    handleFilter() {
      this.listLoading = true
      this.listQuery.pageSize = 10
      this.listQuery.page = 1
      this.$api.getchuangkouList({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leibie,
        state: this.form.state,
        pageNum: 1,
        pageSize: 10,
      }).then(res => {
        console.log(res)
        if (res.code === 0) {
          if (res.data) {
            this.tableData = res.data.pageList
            this.total = res.data.total
            this.listLoading = false
          }else {
            this.$message.warning('暂无数据哦')
            this.listLoading = false
            this.tableData = []
          }
        }else {
          this.$message.error('获取列表失败')
        }
      }).catch(err => {
        console.log(err)
      })
    },
    getList() {
      this.listLoading = false
    },
    chongzhi() {
      this.form.time = ['', '']
      this.form.name = ''
      this.form.state = ''
      this.form.leibie = ''
    },
    handleClick(row) {
      this.$router.push({
        name: 'ckjfxq',
        params: {
          id: row.id,
          state: row.state
        }
      })
    },
    handlejiefang(row) {
      this.$api.getchuangkouchange({
        id: row.id
      }).then(res => {
        this.$message.success('接访成功')
      })
    },
    refresh(val) {
      this.listLoading = true
      this.$api.getchuangkouList({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leibie,
        state: this.form.state,
        pageNum: 1,
        pageSize: 10,
      }).then(res => {
        console.log(res)
        if (res.code === 0) {
          if (res.data) {
            this.tableData = res.data.pageList
            this.total = res.data.total
            this.listLoading = false
          }else {
            this.$message.warning('暂无数据哦')
            this.tableData = []
            this.listLoading = false
          }
        }else {
          this.$message.error('获取列表失败')
        }
      }).catch(err => {
        console.log(err)
      })
    },
    getTime() {
      const nowDate = new Date()
      const date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      }
      let mouth = ''
      // 月
      if (date.month < 10) {
        mouth = '0' + date.month
      } else {
        mouth = date.month
      }
      // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }
      this.form.start = date.year - 1 + '-' + mouth
      this.form.end = date.year + '-' + mouth
    }
  }
}
