import Pagination from '@/components/Pagination/index'
export default {
  name: 'ReviewArrest',
  components: { Pagination },
  data() {
    return {
      form: {
        leibie: '',
        state: '',
        name: '',
        time: ['', '']
      },
      // tableData: [],
      listQuery: {
        page: 1,
        pageSize: 10,
        keyWord: ''
      },
      tableHeader: [
        { filed: 'visitorName', title: '咨询人姓名', colorB: 'colorB', width: '200' },
        { filed: 'mobile', title: '手机号码' },
        { filed: 'createTime', title: '咨询时间' },
        { filed: 'optionValueName', title: '咨询类型' },
        { filed: 'isReply', title: '状态' },
      ],
      tableData: [
      ],
      listLoading: false,
      total: 100
    }
  },
  created() {
    this.getTime()
  },
  mounted() {
  },
  methods: {
    handleFilter() {
      this.listLoading = true
      this.listQuery.page = 1
      this.listQuery.pageSize = 10
      this.$api.getWlzixunList({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leibie,
        state: this.form.state,
        pageNum: 1,
        pageSize: 10
      }).then(res => {
        console.log(res)
        if (res.code === 0) {
          if (res.data) {
            this.tableData = res.data.pageList
            this.total = res.data.total
            this.listLoading = false
          }else {
            this.$message.warning('暂无数据哦')
            this.listLoading = false
          }
        }
      }).catch(err => {
        console.log(err)
      })
    },
    chongzhi() {
      this.form.leibie = ''
      this.form.state = ''
      this.form.time = ['','']
      this.form.name = ''
    },
    handlewatch(row) {
      this.$router.push({
        name: 'wlzxxq',
        params: {
          id: row.id,
          length: row.isReply
        }
      })
    },
    refresh(val) {
      this.$api.getWlzixunList({
        visitorName: this.form.name,
        startTime: this.form.time[0],
        endTime: this.form.time[1],
        optionValue: this.form.leibie,
        state: this.form.state,
        pageNum: val.page,
        pageSize: val.limit
      }).then(res => {
        console.log(res)
        if (res.code === 0) {
          if (res.data) {
            this.tableData = res.data.pageList
            this.total = res.data.total
            this.listLoading = false
          }else {
            this.$message.warning('暂无数据哦')
            this.listLoading = false
          }
        }
      }).catch(err => {
        console.log(err)
      })
    },
    getTime() {
      const nowDate = new Date()
      const date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1,
        date: nowDate.getDate()
      }
      let mouth = ''
      let day = ''
      // 月
      if (date.month < 10) {
        mouth = '0' + date.month
      } else {
        mouth = date.month
      }
      // 日
      if (date.date < 10) {
        day = '0' + date.date
      } else {
        day = date.date
      }
      this.form.time[0] = date.year - 1 + '-' + mouth + '-' + day
      this.form.time[1] = date.year + '-' + mouth + '-' + day
    }
  }
}
