import Pagination from '@/components/Pagination/index'
export default {
  name: 'ReviewArrest',
  components: { Pagination },
  data() {
    return {
      infoPushVisible: false,
      form: {
        state: '',
        leixing: '',
        start: '',
        end: '',
        name: '',
      },
      // tableData: [],
      listQuery: {
        pageNum: 1,
        pageSize: 20,
        keyWord: ''
      },
      tableHeader: [
        { filed: 'name', title: '咨询人姓名', colorB: 'colorB', width: '200' },
        { filed: 'number', title: '手机号码' },
        { filed: 'time', title: '咨询时间' },
        { filed: 'shoufang', title: '受访人姓名' },
        { filed: 'leixing', title: '咨询类型' },
        { filed: 'state', title: '信息来源' },
      ],
      tableData: [
        { name: '张三', number: '手机号码', time: '2020-12-10', leixing: '刑事', state: '未回复', shoufang: '李素' },
        { name: '张三', number: '手机号码', time: '2020-12-10', leixing: '刑事', state: '未回复', shoufang: 1 },
        { name: '张三', number: '手机号码', time: '2020-12-10', leixing: '刑事', state: '未回复', shoufang: 1 },
        { name: '张三', number: '手机号码', time: '2020-12-10', leixing: '刑事', state: '未回复', shoufang: 1 },
      ],
      listLoading: false,
      total: 100
    }
  },
  created() {
    this.getTime()
  },
  mounted() {
    this.getList()
  },
  methods: {
    handleFilter() {
      this.$api.chuangkousousuo({
        params: {
          visitorName: this.form.name,
          startTime: this.form.satrt,
          endTime: this.form.end,
          optionValue: this.form.leixing,
          state: this.form.state,
          pageNum: 1,
          pageSize: 10,
        }
      }).then(res => {
        console.log(res)
      })
    },
    getList() {
      this.listLoading = false
    },
    handleClick(row) {
      this.$router.push({
        name: 'ajhjgzxq',
        params: {
          id: row.id
        }
      })
    },
    handleSee() {
      this.$api.gongxianghuajie().then(res => {
        console.log(res)
      })
    },
    refresh(val) {
    },
    getTime() {
      const nowDate = new Date()
      const date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1
      }
      let mouth = ''
      // 月
      if (date.month < 10) {
        mouth = '0' + date.month
      } else {
        mouth = date.month
      }
      // 日
      // if (date.date < 10) {
      //     day = '0' + date.date
      // } else {
      //     day = date.date
      // }
      this.form.start = date.year - 1 + '-' + mouth
      this.form.end = date.year + '-' + mouth
    }
  }
}
